    {
        if (!extension_loaded('sockets')) {
            throw new \Exception('The sockets extension is not loaded.');
        }
    }

    public function run(): void
    {
        $this->checkRequiredExtensionsExists();
        $this->socket = $this->createServerSocket();
        $this->updatePort();
        $this->output->writeln('Server started on ' . $this->host . ':' . $this->port);

        if ($this->afterCallback) {
            $this->afterCallback();
        }

        while (!$this->stop) {
            $this->acceptNewConnections();
            $this->handleClientRequests();

            if ($this->tickerCallback) {
                $this->tickerCallback();
            }

            usleep(100000);
        }

        $this->output->writeln('Server stopped');
    }

    private function createServerSocket(): resource
    {
        $socket = stream_socket_server('tcp://' . $this->host . ':' . $this->port, $errno, $errstr, STREAM_SERVER_BIND | STREAM_SERVER_LISTEN);
        if (!$socket) {
            throw new \Exception('Could not bind to ' . $this->host . ':' . $this->port . '. Error: ' . $errstr);
        }

        stream_set_blocking($socket, false);

        return $socket;
    }

    private function updatePort(): void
    {
        $socket = $this->socket;
        $socketAddress = stream_socket_get_name($socket, false);
        if ($socketAddress) {
            $this->port = (int) substr($socketAddress, strrpos($socketAddress, ':') + 1);
        }
    }

    private function acceptNewConnections(): void
    {
        while ($newSocket = @stream_socket_accept($this->socket, 0)) {
            stream_set_blocking($newSocket, false);
            $this->clientSockets[] = $newSocket;
            $this->output->writeln('New client connected');
        }
    }

    private function handleClientRequests(): void
    {
        foreach ($this->clientSockets as $key => $clientSocket) {
            if (feof($clientSocket)) {
                $this->closeClientSocket($clientSocket, $key);
                continue;
            }

            $request = $this->readClientRequest($clientSocket);
            if ($request) {
                $response = $this->routerCallback($request);
                $this->sendResponse($clientSocket, $response);
            }
        }
    }

    private function readClientRequest($clientSocket): string
    {
        $request = '';
        while (!feof($clientSocket)) {
            $request .= fgets($clientSocket, 1024);
            if (strpos($request, "\r\n\r\n") !== false) {
                break;
            }
        }

        return $request;
    }

    private function parseRequest(string $request): array
    {
        $lines = explode("\r\n", $request);
        $path = explode(' ', $lines[0])[1];
        $payload = substr($request, strpos($request, "\r\n\r\n") + 4);

        return [$path, $payload];
    }

    private function sendResponse($clientSocket, Response $response): void
    {
        $headers = $response->getHeaders();
        $body = $response->getBody();

        $responseString = "HTTP/1.1 " . $response->getStatusCode() . " " . $response->getReasonPhrase() . "\r\n";
        foreach ($headers as $header => $value) {
            $responseString .= $header . ': ' . $value . "\r\n";
        }
        $responseString .= "\r\n" . $body;

        fwrite($clientSocket, $responseString);
    }

    private function closeClientSocket($clientSocket, int $key): void
    {
        fclose($clientSocket);
        unset($this->clientSockets[$key]);
        $this->output->writeln('Client disconnected');
    }

    public function afterRun(Closure $param): void
    {
        $this->afterCallback = $param;
    }

    public function ticker(Closure $param): void
    {
        $this->tickerCallback = $param;
    }

    public function router(Closure $param): void
    {
        $this->routerCallback = $param;
    }

    public function stop(): void
    {
        $this->stop = true;
    }
