class AlipayService {
    private $appId;
    private $rsaPrivateKey;
    private $alipayRootCertSn;
    private $appCertSn;
    private $charset = 'utf-8';

    public function __construct(string $appid, string $saPrivateKey) {
        $this->appId = $appid;
        $this->rsaPrivateKey = $saPrivateKey;
    }

    public function setAlipayRootCertSn(string $alipayRootCertSn) {
        $this->alipayRootCertSn = $alipayRootCertSn;
    }

    public function setAppCertSn(string $appCertSn) {
        $this->appCertSn = $appCertSn;
    }

    public function doTransfer(float $totalFee, string $outTradeNo, string $account, string $realName, string $remark='') {
        $params = [
            'out_biz_no' => $outTradeNo,
            'trans_amount' => $totalFee,
            'payee_type' => 'ALIPAY_LOGONID',
            'payee_account' => $account,
            'payee_real_name' => $realName,
            'remark' => $remark,
        ];

        $params['sign'] = $this->generateSign($params);

        $url = 'https://openapi.alipay.com/gateway.do';
        $result = $this->curlPost($url, $params);

        return json_decode($result, true);
    }

    private function curlPost(string $url='', mixed $postData='', array $options=array()) {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Content-Type: application/x-www-form-urlencoded;charset=' . $this->charset,
        ]);
        curl_setopt_array($ch, $options);
        $result = curl_exec($ch);
        curl_close($ch);

        return $result;
    }

    private function generateSign(array $params, string $signType="RSA") {
        ksort($params);
        $signContent = $this->getSignContent($params);
        return $this->sign($signContent, $signType);
    }

    private function sign(string $data, string $signType="RSA") {
        $res = "-----BEGIN RSA PRIVATE KEY-----\n" .
            wordwrap($this->rsaPrivateKey, 64, "\n", true) .
            "\n-----END RSA PRIVATE KEY-----";
        openssl_sign($data, $sign, $res, OPENSSL_ALGO_SHA256);
        return base64_encode($sign);
    }

    private function checkEmpty(mixed $value) {
        if ($value === '' || $value === null) {
            return true;
        }
        return false;
    }

    private function getSignContent(array $params) {
        ksort($params);
        $stringToBeSigned = '';
        $i = 0;
        foreach ($params as $k => $v) {
            if ($this->checkEmpty($v) || $k == 'sign' || $k == 'sign_type') {
                continue;
            }
            if ($i == 0) {
                $stringToBeSigned .= $k . '=' . $v;
            } else {
                $stringToBeSigned .= '&' . $k . '=' . $v;
            }
            $i++;
        }
        unset($k, $v);
        return $stringToBeSigned;
    }

    private function characet(string $data, string $targetCharset) {
        $fileType = mb_detect_encoding($data, array('UTF-8','GBK','LATIN1','BIG5'));
        if ($fileType != $targetCharset) {
            $data = mb_convert_encoding($data, $targetCharset, $fileType);
        }
        return $data;
    }
}
