            $header = explode(':', $header, 2);
            if (count($header) < 2) return strlen($header[0]);
            $this->header_[$header[0]] = trim($header[1]);
            return strlen($header);
        });
        $response = curl_exec($ch);
        $this->httpCode_ = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $this->httpDesc_ = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        if ($response === false) {
            return false;
        }
        $json = json_decode($response, true);
        if ($json === null) {
            return false;
        }
        return $json;
    }
