    public function __construct($timeout = 3600) {
        $this->timeout = $timeout;
    }

    public function request($path, $method, $headers = null, $body = null) {
        $url = 'http://' . $this->getHost($path) . '/' . ltrim($path, '/');
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        if ($headers) {
            curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);
        }
        if ($body) {
            curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
        }
        $response = curl_exec($ch);
        $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        if ($httpCode == 200) {
            if ($method == 'GET') {
                return $response;
            } else {
                return $this->response($response);
            }
        } else {
            $this->error($httpCode, $response);
            return false;
        }
    }

    private function getHost($path) {
        if (strpos($path, '/list/') === 0) {
            return self::QINIU_RSF_HOST;
        } elseif (strpos($path, '/stat/') === 0 || strpos($path, '/delete/') === 0) {
            return self::QINIU_RS_HOST;
        } else {
            return self::QINIU_UP_HOST;
        }
    }

    private function response($text) {
        $headerSize = strpos($text, "\r\n\r\n");
        $header = substr($text, 0, $headerSize);
        $body = substr($text, $headerSize + 4);
        $headers = explode("\r\n", $header);
        $data = array();
        foreach ($headers as $header) {
            $pos = strpos($header, ':');
            if ($pos !== false) {
                $key = trim(substr($header, 0, $pos));
                $value = trim(substr($header, $pos + 1));
                $data[$key] = $value;
            }
        }
        $data['body'] = $body;
        return $data;
    }

    private function error($header, $body) {
        $this->error = $header;
        $this->errorStr = $body;
    }
}
