        return $this->response_headers;
    }

    public function get_response_body() {
        return $this->response_body;
    }

    public function get_response_code() {
        return $this->response_code;
    }

    protected function parse_headers(string $raw_headers) {
        $headers = array();
        $header_lines = explode("\r\n", $raw_headers);
        foreach ($header_lines as $header_line) {
            if (strpos($header_line, ':') !== false) {
                list($key, $value) = explode(':', $header_line, 2);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }

    protected function parse_response() {
        $response_class = $this->response_class;
        return new $response_class($this);
    }
