    {
        if (!$config->get('processNotifyUrl')) {
            throw new Exception('processNotifyUrl is not configured');
        }

        $this->config = $config;
        $this->client = new Client([
            'base_uri' => $config->get('endpoint'),
            'timeout' => $config->get('timeout'),
        ]);
