        try {
            $response = $this->client->post($this->config->get('processPath'), [
                'json' => $params,
                'headers' => [
                    'Authorization' => 'Bearer ' . $this->config->get('apiKey'),
                ],
            ]);

            return json_decode($response->getBody(), true);
        } catch (RequestException $e) {
            // Handle request exception
            return false;
        }
