class URLChecker {
    private const POLL_INTERVAL_MS = 1000;
    private const CONNECT_TIMEOUT_MS = 5000;

    public function waitUntilAvailable(int $timeout_in_ms, string $url): self {
        $start_time = time();
        while (true) {
            if ($this->getHTTPResponseCode($url) === 200) {
                return $this;
            }
            if (time() - $start_time >= $timeout_in_ms / 1000) {
                throw new TimeoutException("URL $url did not become available within $timeout_in_ms ms");
            }
            usleep(self::POLL_INTERVAL_MS * 1000);
        }
    }

    public function waitUntilUnavailable(int $timeout_in_ms, string $url): self {
        $start_time = time();
        while (true) {
            if ($this->getHTTPResponseCode($url) !== 200) {
                return $this;
            }
            if (time() - $start_time >= $timeout_in_ms / 1000) {
                throw new TimeoutException("URL $url did not become unavailable within $timeout_in_ms ms");
            }
            usleep(self::POLL_INTERVAL_MS * 1000);
        }
    }

    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT_MS, self::CONNECT_TIMEOUT_MS);
        curl_setopt($ch, CURLOPT_TIMEOUT_MS, self::CONNECT_TIMEOUT_MS);
        $response = curl_exec($ch);
        $http_code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        return $response ? $http_code : null;
    }
}
