    private $appId;
    private $rsaPrivateKey;
    private $charset = 'utf-8';

    public function __construct(string $appid, string $saPrivateKey) {
        $this->appId = $appid;
        $this->rsaPrivateKey = $saPrivateKey;
    }

    public function doQuery(string $outBizBo='', string $orderId='') {
        $params = [
            'app_id' => $this->appId,
            'method' => 'alipay.fund.trans.toaccount.transfer',
            'charset' => $this->charset,
            'sign_type' => 'RSA2',
            'timestamp' => date('Y-m-d H:i:s'),
            'version' => '1.0',
            'biz_content' => json_encode([
                'out_biz_no' => $outBizBo,
                'payee_type' => 'ALIPAY_LOGONID',
                'payee_account' => '',
                'amount' => '',
                'payer_show_name' => '',
                'payee_real_name' => '',
                'remark' => ''
            ])
        ];

        $params['sign'] = $this->generateSign($params);

        return $this->curlPost('https://openapi.alipay.com/gateway.do', $params);
    }

    private function generateSign(array $params, string $signType='RSA') {
        ksort($params);
        $stringToBeSigned = $this->getSignContent($params);
        return $this->sign($stringToBeSigned, $signType);
    }

    private function sign(string $data, string $signType='RSA') {
        $res = "-----BEGIN RSA PRIVATE KEY-----\n" .
            wordwrap($this->rsaPrivateKey, 64, "\n", true) .
            "\n-----END RSA PRIVATE KEY-----";
        openssl_sign($data, $sign, $res, OPENSSL_ALGO_SHA256);
        return base64_encode($sign);
    }

    private function checkEmpty(mixed $value) {
        if ($value === '' || $value === null) {
            return true;
        }
        return false;
    }

    private function getSignContent(array $params) {
        $signContent = '';
        ksort($params);
        foreach ($params as $key => $value) {
            if ($this->checkEmpty($value)) {
                continue;
            }
            $signContent .= $key . '=' . $value . '&';
        }
        return rtrim($signContent, '&');
    }

    private function characet(string $data, string $targetCharset) {
        if (!empty($data)) {
            $fileType = mb_detect_encoding($data, array('UTF-8','GBK','LATIN1','BIG5'));
            if ($fileType != $targetCharset) {
                $data = mb_convert_encoding($data, $targetCharset, $fileType);
            }
        }
        return $data;
    }

    private function curlPost(string $url='', mixed $postData='', array $options=[]) {
        if (empty($url) || empty($postData)) {
            return false;
        }

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Content-Type: application/x-www-form-urlencoded;charset=' . $this->charset
        ]);

        if (!empty($options)) {
            curl_setopt_array($ch, $options);
        }

        $response = curl_exec($ch);
        curl_close($ch);

        return $response;
    }
}
