    public static function curlWrite($h, $d) {
        if ($this->curlFH === false) {
            return 0;
        }
        $len = strlen($d);
        if ($this->curlDataWritten + $len > 1024*1024) {
            return 0;
        }
        $this->curlDataWritten += $len;
        return fwrite($this->curlFH, $d);
    }

    public function getURL($url, $tempfile) {
        $this->lastURLError = false;
        if (!function_exists('curl_init')) {
            if (function_exists('file_get_contents')) {
                $data = file_get_contents($url);
                if ($data !== false) {
                    file_put_contents($tempfile, $data);
                    return true;
                }
            }
            $this->lastURLError = 'cURL not available and file_get_contents failed';
            return false;
        }
        $ch = curl_init($url);
        if ($ch === false) {
            $this->lastURLError = 'cURL init failed';
            return false;
        }
        $this->curlFH = fopen($tempfile, 'wb');
        if ($this->curlFH === false) {
            $this->lastURLError = 'Failed to open temporary file';
            curl_close($ch);
            return false;
        }
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, array($this, 'curlWrite'));
        curl_setopt($ch, CURLOPT_FAILONERROR, true);
