    public function request(string $uri, string $method = 'GET', array $parameters = [], array $headers = [], bool $multipart = false): string {
        $this->requestArguments = [
            'uri' => $uri,
            'method' => $method,
            'parameters' => $parameters,
            'headers' => $headers,
            'multipart' => $multipart,
        ];
        $this->responseBody = '';
        $this->responseHeader = [];
        $this->responseHttpCode = 0;
        $this->responseClientError = null;
        $this->responseClientInfo = [];

        $ch = curl_init();
        curl_setopt_array($ch, $this->curlOptions);
        curl_setopt($ch, CURLOPT_URL, $uri);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);
        curl_setopt($ch, CURLOPT_HTTPHEADER, $this->prepareRequestHeaders($headers));
        curl_setopt($ch, CURLOPT_HEADERFUNCTION, [$this, 'fetchResponseHeader']);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $multipart ? $this->prepareMultipartFormData($parameters) : http_build_query($parameters));

        $response = curl_exec($ch);
        $this->responseClientError = curl_error($ch);
        $this->responseClientInfo = curl_getinfo($ch);
        curl_close($ch);

        if ($response !== false) {
            $this->responseBody = $response;
        }

        if ($this->logger) {
            $this->logger->logRequest($this->getRequestArguments());
            $this->logger->logResponse($this->getResponse());
        }

        return $this->responseBody;
    }

    public function getResponse(): array {
        return [
            'headers' => $this->responseHeader,
            'httpCode' => $this->responseHttpCode,
            'body' => $this->responseBody,
            'clientInfo' => $this->responseClientInfo,
        ];
    }

    public function setCurlOptions(array $curlOptions): void {
        $this->curlOptions = array_merge($this->curlOptions, $curlOptions);
    }

    public function setLogger(object $logger): void {
        $this->logger = $logger;
    }

    public function getResponseBody(): string {
        return $this->responseBody;
    }

    public function getResponseHeader(): array {
        return $this->responseHeader;
    }

    public function getResponseHttpCode(): int {
        return $this->responseHttpCode;
    }

    public function getResponseClientError(): ?string {
        return $this->responseClientError;
    }

    private function fetchResponseHeader($curl, string $header): int {
        $matches = [];
        if (preg_match('/^HTTP\/\S+ (\d+) .+/', $header, $matches)) {
            $this->responseHttpCode = (int) $matches[1];
        } else {
            $key = explode(':', $header, 2)[0];
            $this->responseHeader[$key] = trim(explode(':', $header, 2)[1]);
        }
        return strlen($header);
    }

    private function prepareRequestHeaders(array $headers): array {
        return array_merge($this->requestHeader, $headers);
    }

    private function getResponseClientInfo(): array {
        return $this->responseClientInfo;
    }

    private function getRequestArguments(): array {
        return $this->requestArguments;
    }

    private function prepareMultipartFormData(array $parameters): string {
        $boundary = uniqid();
        $multipartData = '';
        foreach ($parameters as $name => $value) {
            $multipartData .= "--$boundary\r\n";
            if (is_array($value)) {
                foreach ($value as $fileName => $fileContent) {
                    $multipartData .= "Content-Disposition: form-data; name=\"$name\"; filename=\"$fileName\"\r\n";
                    $multipartData .= "Content-Type: application/octet-stream\r\n\r\n";
