class CurlDownloader {
    private $tempDir;
    private $outputFile;

    public function setTempDir(string $tempDir) {
        $this->tempDir = $tempDir;
    }

    public function download(string $url): bool {
        $tempDir = $this->tempDir ?? sys_get_temp_dir();
        $this->outputFile = tempnam($tempDir, 'download_');

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FILE, fopen($this->outputFile, 'w'));

        $result = curl_exec($ch);
        if ($result === false) {
            throw new ErrorException(curl_error($ch));
        }

        curl_close($ch);
        return true;
    }

    public function getFilename(): string {
        if ($this->outputFile === null) {
            throw new RuntimeException('Download not completed.');
        }
        return $this->outputFile;
    }

    public function cleanup() {
        if ($this->outputFile !== null) {
            unlink($this->outputFile);
        }
    }
}
