class Downloader {
    public static function start(string|array $url, string $saveFile, int $timeout = 10): array {
        if (!is_array($url)) {
            $url = [$url];
        }
        $result = [];
        foreach ($url as $u) {
            $headers = get_headers($u, 1);
            if (isset($headers['Content-Length'])) {
                $length = (int)$headers['Content-Length'];
            } else {
                $length = 0;
            }
            if (isset($headers['Accept-Ranges']) && $headers['Accept-Ranges'] == 'bytes') {
                $supportRange = true;
            } else {
                $supportRange = false;
            }
            if (isset($headers['Content-Encoding']) && $headers['Content-Encoding'] == 'gzip') {
                $gzip = true;
            } else {
                $gzip = false;
            }
            if (file_exists($saveFile)) {
                $existsLength = filesize($saveFile);
            } else {
                $existsLength = 0;
            }
            if ($existsLength >= $length) {
                $result[] = ['url' => $u, 'status' => 'file already exists'];
                continue;
            }
            if ($supportRange) {
                $result[] = self::fileDownloadCurl($u, $saveFile, $supportRange, $existsLength, $length);
            } else {
                $result[] = self::fileDownloadFopen($u, $saveFile, $headerSize);
            }
            if ($gzip) {
