        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        if ($supportRange) {
            if ($existsLength > 0) {
                curl_setopt($ch, CURLOPT_RANGE, "bytes=$existsLength-$length");
            } else {
                curl_setopt($ch, CURLOPT_RANGE, "bytes=0-$length");
            }
