class HproseHttpClient extends HproseBaseHttpClient {
    private $curl;
    private $url;
    private $header = array();
    private $keepAlive = false;
    private $keepAliveTimeout = 0;
    private $proxy;
    private $timeout = 0;

    public function __construct(string $url = '') {
        $this->curl = curl_init();
        $this->url = $url;
        curl_setopt($this->curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($this->curl, CURLOPT_HEADER, false);
        curl_setopt($this->curl, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($this->curl, CURLOPT_SSL_VERIFYHOST, false);
        curl_setopt($this->curl, CURLOPT_HTTPHEADER, array('Expect:'));
    }

    private function formatCookie(array $cookies) {
        $cookie = '';
        foreach ($cookies as $name => $value) {
            $cookie .= $name . '=' . $value . '; ';
        }
        return $cookie;
    }

    public function send(string $request) {
        $headers = array('Content-Type: application/hprose');
        if (!empty($this->header)) {
            $headers = array_merge($headers, $this->header);
        }
        if (!empty($this->cookies)) {
            $headers[] = 'Cookie: ' . $this->formatCookie($this->cookies);
        }
        curl_setopt($this->curl, CURLOPT_HTTPHEADER, $headers);
        curl_setopt($this->curl, CURLOPT_URL, $this->url);
        curl_setopt($this->curl, CURLOPT_POSTFIELDS, $request);
        curl_setopt($this->curl, CURLOPT_TIMEOUT_MS, $this->timeout);
        if ($this->keepAlive) {
            curl_setopt($this->curl, CURLOPT_FORBID_REUSE, false);
            curl_setopt($this->curl, CURLOPT_FRESH_CONNECT, false);
            curl_setopt($this->curl, CURLOPT_TIMEOUT_MS, $this->keepAliveTimeout);
        } else {
            curl_setopt($this->curl, CURLOPT_FORBID_REUSE, true);
            curl_setopt($this->curl, CURLOPT_FRESH_CONNECT, true);
        }
        if (!empty($this->proxy)) {
            curl_setopt($this->curl, CURLOPT_PROXY, $this->proxy);
        }
        $response = curl_exec($this->curl);
        if (curl_errno($this->curl)) {
            throw new HproseException(curl_error($this->curl));
        }
        $this->parseResponseHeaders($response);
        return $response;
    }

    public function __destruct() {
        curl_close($this->curl);
    }
}
