class timthumb {
    private $curlFH = false;
    private $curlDataWritten = 0;
    private $lastURLError = false;

    public static function curlWrite($h, $d) {
        if ($this->curlFH === false) {
            return 0;
        }
        $len = strlen($d);
        if ($this->curlDataWritten + $len > 1000000) {
            return 0;
        }
        $this->curlDataWritten += $len;
        fwrite($this->curlFH, $d);
        return $len;
    }

    public function getURL($url, $tempfile) {
        $this->lastURLError = false;
        if (function_exists('curl_init')) {
            $ch = curl_init($url);
            if ($ch) {
                $this->curlFH = fopen($tempfile, 'wb');
                curl_setopt($ch, CURLOPT_FILE, $this->curlFH);
                curl_setopt($ch, CURLOPT_WRITEFUNCTION, array($this, 'curlWrite'));
                curl_setopt($ch, CURLOPT_FAILONERROR, true);
                curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
                curl_setopt($ch, CURLOPT_MAXREDIRS, 5);
                curl_setopt($ch, CURLOPT_TIMEOUT, 30);
                curl_exec($ch);
                $info = curl_getinfo($ch);
                $http_code = $info['http_code'];
                curl_close($ch);
                fclose($this->curlFH);
                $this->curlFH = false;
                if ($http_code != 200) {
                    $this->lastURLError = 'HTTP error: ' . $http_code;
                    return false;
                }
                return true;
            }
        }
        $data = @file_get_contents($url);
        if ($data === false) {
            $this->lastURLError = 'file_get_contents failed';
            return false;
        }
        if (strlen($data) > 1000000) {
            $this->lastURLError = 'file_get_contents returned too much data';
            return false;
        }
        file_put_contents($tempfile, $data);
        return true;
    }
