    {
        $this->crawler = $crawler;
    }

    public static function loadFromUrl(string $url, ?Options $options = null, ?ClientInterface $client = null, ?RequestInterface $request = null): Dom
    {
        if ($client === null) {
            $client = new Client();
        }

        if ($request === null) {
            $request = new Request('GET', $url);
        }

        try {
            $response = $client->send($request, $options);
        } catch (RequestException $e) {
            throw new \Exception('Error fetching content from URL: ' . $e->getMessage());
        }

        if ($response->getStatusCode() !== 200) {
            throw new \Exception('Error fetching content from URL: ' . $response->getReasonPhrase());
        }

        $crawler = new Crawler((string) $response->getBody());

        return new self($crawler);
    }

    public function getCrawler(): Crawler
    {
        return $this->crawler;
    }
}
