    $title = isset($titleMatch[1]) ? trim($titleMatch[1]) : '';

    preg_match('/<meta property="og:image" content="(.*?)"/i', $html, $coverMatch);
    $cover = isset($coverMatch[1]) ? 'https://images.weserv.nl/?url=' . urlencode($coverMatch[1]) : '';

