function get_favicon($url, $cache_time, $default_ico, $basename = "favicon.ico") {
    $cache_dir = sys_get_temp_dir() . '/favicon_cache/';
    $cache_file = $cache_dir . md5($url) . '_' . $basename;

    if (!file_exists($cache_file) || (time() - filemtime($cache_file)) > $cache_time) {
        $ch = curl_init($url . '/' . $basename);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, 5);
        curl_setopt($ch, CURLOPT_NOBODY, true);
        curl_exec($ch);

        $info = curl_getinfo($ch);
        curl_close($ch);

        if ($info['http_code'] == 200 && $info['content_type'] == 'image/x-icon') {
            $ch = curl_init($url . '/' . $basename);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
            curl_setopt($ch, CURLOPT_TIMEOUT, 5);
            $favicon = curl_exec($ch);
            curl_close($ch);

            if ($favicon) {
                if (!is_dir($cache_dir)) {
                    mkdir($cache_dir, 0777, true);
                }
                file_put_contents($cache_file, $favicon);
            }
        }
    }

    if (file_exists($cache_file)) {
        header('Content-Type: image/x-icon');
        header('Cache-Control: public, max-age=' . $cache_time);
        header('Expires: ' . gmdate('D, d M Y H:i:s T', time() + $cache_time));
        readfile($cache_file);
    } else {
        header('Content-Type: image/x-icon');
        readfile($default_ico);
