class GoogleTranslate {
    private static $API_URL = 'https://translate.google.com/translate_a/single';

    public static function translate($source, $target, $text) {
        if (strlen($text) > 9000) {
            throw new Exception('Input text exceeds 9000 characters.');
        }
        $json = self::requestTranslation($source, $target, $text);
        return self::getSentencesFromJSON($json);
    }

    private static function requestTranslation($source, $target, $text) {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, self::$API_URL);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query([
            'client' => 't',
            'sl' => $source,
            'tl' => $target,
            'hl' => $target,
            'dt' => 'at',
            'dt' => 'bd',
            'dt' => 'ex',
            'dt' => 'ld',
            'dt' => 'md',
            'dt' => 'qca',
            'dt' => 'rw',
            'dt' => 'rm',
            'dt' => 'ss',
            'dt' => 't',
            'ie' => 'UTF-8',
            'oe' => 'UTF-8',
            'otf' => 1,
            'ssel' => 0,
            'tsel' => 0,
            'kc' => 1,
            'tk' => '',
            'q' => $text
        ]));
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        $response = curl_exec($ch);
        curl_close($ch);
        return $response;
    }

    private static function getSentencesFromJSON($json) {
        $data = json_decode($json, true);
        $sentences = [];
        foreach ($data[0] as $sentence) {
            $sentences[] = $sentence[0];
        }
        return implode(' ', $sentences);
    }
}
