use GuzzleHttp\Client;

class Ollama
{
    /**
     * New service instance.
     */
    public function __construct(
        protected string $model,
        protected string $prompt,
        protected float $temperature,
        protected bool $stream,
        protected bool $raw,
    ) {}

    /**
     * Set LLM prompt text.
     */
    public function ask(): string
    {
        $httpClient = new Client;

        $endpoint = core()->getConfigData('general.magic_ai.settings.api_domain').'/api/generate';

        $result = $httpClient->request('POST', $endpoint, [
            'headers' => [
                'Accept' => 'application/json',
            ],
            'json'    => [
                'model'  => $this->model,
                'prompt' => $this->prompt,
                'raw'    => $this->raw,
                'stream' => $this->stream,
            ],
        ]);

        $result = json_decode($result->getBody()->getContents(), true);

        return $result['response'];
    }
}

use GuzzleHttp\Client;
use GuzzleHttp\Exception\GuzzleException;

class EpayHttpClient
{
    protected array $epay;
    protected Client $client;

    public function __construct(array $epayConfig)
    {
        $this->epay = $epayConfig;
        $this->client = new Client();
    }

    public function sendPaymentRequest(array $data): array
    {
        try {
            $res = $this->client->request('POST', $this->epay['apiurl'] . 'mapi.php', ['form_params' => $data]);

            if ($res->getStatusCode() !== 200) {
                throw new Exception();
            }

            $resData = json_decode($res->getBody()->__toString(), true);

            if ($resData['code'] !== 1 || !isset($resData['payurl'])) {
                throw new Exception();
            }

            return [
                'success' => true,
                'payurl' => $resData['payurl']
            ];
        } catch (Exception|GuzzleException) {
            return [
                'success' => false,
                'error' => '请求支付失败'
            ];
        }
    }
}

class ReCaptchaSocketPostRequestMethod implements ReCaptchaRequestMethod
{
    const RECAPTCHA_HOST = 'www.google.com';
    const SITE_VERIFY_PATH = '/recaptcha/api/siteverify';
    const BAD_REQUEST = '{"success": false, "error-codes": ["invalid-request"]}';
    const BAD_RESPONSE = '{"success": false, "error-codes": ["invalid-response"]}';
    private $socket;

    public function __construct(ReCaptchaSocket $socket = null)
    {
        if (!is_null($socket)) {
            $this->socket = $socket;
        } else {
            $this->socket = new ReCaptchaSocket();
        }
    }

    public function submit(ReCaptchaRequestParameters $params)
    {
        $errno = 0;
        $errstr = '';

        if (false === $this->socket->fsockopen('ssl://' . self::RECAPTCHA_HOST, 443, $errno, $errstr, 30)) {
            return self::BAD_REQUEST;
        }

        $content = $params->toQueryString();

        $request = "POST " . self::SITE_VERIFY_PATH . " HTTP/1.1\r\n";
        $request .= "Host: " . self::RECAPTCHA_HOST . "\r\n";
        $request .= "Content-Type: application/x-www-form-urlencoded\r\n";
        $request .= "Content-length: " . strlen($content) . "\r\n";
        $request .= "Connection: close\r\n\r\n";
        $request .= $content . "\r\n\r\n";

        $this->socket->fwrite($request);
        $response = '';

        while (!$this->socket->feof()) {
            $response .= $this->socket->fgets(4096);
        }

        $this->socket->fclose();

        if (0 !== strpos($response, 'HTTP/1.1 200 OK')) {
            return self::BAD_RESPONSE;
        }

        $parts = preg_split("#\n\s*\n#Uis", $response);

        return $parts[1];
    }
}

use Volc\Base\V4Curl;
use GuzzleHttp\Client;

class ImageX extends V4Curl
{
    public function upload(string $uploadHost, $storeInfo, string $filePath)
    {
        if (!file_exists($filePath)) {
            return -1;
        }
        $content = file_get_contents($filePath);
        $crc32 = sprintf("%08x", crc32($content));

        $body = fopen($filePath, "r");
        $tosClient = new Client([
            'base_uri' => "https://" . $uploadHost,
            'timeout' => 5.0,
        ]);

        $response = $tosClient->request('PUT', $storeInfo["StoreUri"], ["body" => $body, "headers" => ['Authorization' => $storeInfo["Auth"], 'Content-CRC32' => $crc32]]);
        $uploadResponse = json_decode((string) $response->getBody(), true);
        if (!isset($uploadResponse["success"]) || $uploadResponse["success"] != 0) {
            return -2;
        }
        return 0;
    }
}

namespace Org\Net;

class Http
{
    /**
     * 使用 fsockopen 通过 HTTP 协议直接访问(采集)远程文件
     * 如果主机或服务器没有开启 CURL 扩展可考虑使用
     * fsockopen 比 CURL 稍慢,但性能稳定
     * @static
     * @access public
     * @param string $url 远程URL
     * @param array $conf 其他配置信息
     *        int   limit 分段读取字符个数
     *        string post  post的内容,字符串或数组,key=value&形式
     *        string cookie 携带cookie访问,该参数是cookie内容
     *        string ip    如果该参数传入,$url将不被使用,ip访问优先
     *        int    timeout 采集超时时间
     *        bool   block 是否阻塞访问,默认为true
     * @return mixed
     */
    public static function fsockopenDownload($url, $conf = array())
    {
        $return = '';
        if (!is_array($conf)) {
            return $return;
        }

        $matches                                       = parse_url($url);
        !isset($matches['host']) && $matches['host']   = '';
        !isset($matches['path']) && $matches['path']   = '';
        !isset($matches['query']) && $matches['query'] = '';
        !isset($matches['port']) && $matches['port']   = '';
        $host                                          = $matches['host'];
        $path                                          = $matches['path'] ? $matches['path'] . ($matches['query'] ? '?' . $matches['query'] : '') : '/';
        $port                                          = !empty($matches['port']) ? $matches['port'] : 80;

        $conf_arr = array(
            'limit'   => 0,
            'post'    => '',
            'cookie'  => '',
            'ip'      => '',
            'timeout' => 15,
            'block'   => true,
        );

        foreach (array_merge($conf_arr, $conf) as $k => $v) {
            ${$k} = $v;
        }

        if ($post) {
            if (is_array($post)) {
                $post = http_build_query($post);
            }
            $out = "POST $path HTTP/1.0\r\n";
            $out .= "Accept: */*\r\n";
            $out .= "Accept-Language: zh-cn\r\n";
            $out .= "Content-Type: application/x-www-form-urlencoded\r\n";
            $out .= "User-Agent: $_SERVER[HTTP_USER_AGENT]\r\n";
            $out .= "Host: $host\r\n";
            $out .= 'Content-Length: ' . strlen($post) . "\r\n";
            $out .= "Connection: Close\r\n";
            $out .= "Cache-Control: no-cache\r\n";
            $out .= "Cookie: $cookie\r\n\r\n";
            $out .= $post;
        } else {
            $out = "GET $path HTTP/1.0\r\n";
            $out .= "Accept: */*\r\n";
            $out .= "Accept-Language: zh-cn\r\n";
            $out .= "User-Agent: $_SERVER[HTTP_USER_AGENT]\r\n";
            $out .= "Host: $host\r\n";
            $out .= "Connection: Close\r\n";
            $out .= "Cookie: $cookie\r\n\r\n";
        }
        $fp = @fsockopen(($ip ? $ip : $host), $port, $errno, $errstr, $timeout);
        if (!$fp) {
            return '';
        } else {
            stream_set_blocking($fp, $block);
            stream_set_timeout($fp, $timeout);
            @fwrite($fp, $out);
            $status = stream_get_meta_data($fp);
            if (!$status['timed_out']) {
                while (!feof($fp)) {
                    if (($header = @fgets($fp)) && ("\r\n" == $header || "\n" == $header)) {
                        break;
                    }
                }

                $stop = false;
                while (!feof($fp) && !$stop) {
                    $data = fread($fp, (0 == $limit || $limit > 8192 ? 8192 : $limit));
                    $return .= $data;
                    if ($limit) {
                        $limit -= strlen($data);
                        $stop = $limit <= 0;
                    }
                }
            }
            @fclose($fp);
            return $return;
        }
    }
}

use Mpdf\Log\Context as LogContext;
use Mpdf\PsrHttpMessageShim\Response;
use Mpdf\PsrHttpMessageShim\Stream;
use Mpdf\PsrLogAwareTrait\PsrLogAwareTrait;
use Psr\Http\Message\RequestInterface;
use Psr\Log\LoggerInterface;

class SocketHttpClient implements \Mpdf\Http\ClientInterface, \Psr\Log\LoggerAwareInterface
{
    use PsrLogAwareTrait;

    public function __construct(LoggerInterface $logger)
    {
        $this->logger = $logger;
    }

    public function sendRequest(RequestInterface $request)
    {
        if (null === $request->getUri()) {
            return (new Response());
        }

        $url = $request->getUri();

        if (is_string($url)) {
            $url = new Uri($url);
        }

        $timeout = 1;

        $file = $url->getPath() ?: '/';
        $scheme = $url->getScheme();
        $port = $url->getPort() ?: 80;
        $prefix = '';

        if ($scheme === 'https') {
            $prefix = 'ssl://';
            $port = $url->getPort() ?: 443;
        }

        $query = $url->getQuery();
        if ($query) {
            $file .= '?' . $query;
        }

        $socketPath = $prefix . $url->getHost();

        $this->logger->debug(sprintf('Opening socket on %s:%s of URL "%s"', $socketPath, $port, $request->getUri()), ['context' => LogContext::REMOTE_CONTENT]);

        $response = new Response();

        if (!($fh = @fsockopen($socketPath, $port, $errno, $errstr, $timeout))) {
            $this->logger->error(sprintf('Socket error "%s": "%s"', $errno, $errstr), ['context' => LogContext::REMOTE_CONTENT]);
            return $response;
        }

        $getRequest = 'GET ' . $file . ' HTTP/1.1' . "\r\n" .
            'Host: ' . $url->getHost() . " \r\n" .
            'Connection: close' . "\r\n\r\n";

        fwrite($fh, $getRequest);

        $httpHeader = fgets($fh, 1024);
        if (!$httpHeader) {
            return $response;
        }

        preg_match('@HTTP/(?P<protocolVersion>[\d\.]+) (?P<httpStatusCode>[\d]+) .*@', $httpHeader, $parsedHeader);

        if (!$parsedHeader) {
            return $response;
        }

        $response = $response->withStatus($parsedHeader['httpStatusCode']);

        while (!feof($fh)) {
            $s = fgets($fh, 1024);
            if ($s === "\r\n") {
                break;
            }
            preg_match('/^(?P<headerName>.*?): ?(?P<headerValue>.*)$/', $s, $parsedHeader);
            if (!$parsedHeader) {
                continue;
            }
            $response = $response->withHeader($parsedHeader['headerName'], trim($parsedHeader['headerValue']));
        }

        $body = '';

        while (!feof($fh)) {
            $line = fgets($fh, 1024);
            $body .= $line;
        }

        fclose($fh);

        $stream = Stream::create($body);
        $stream->rewind();

        return $response
            ->withBody($stream);
    }
}

function start_rabbitmq_consumer($host, $port, $user, $pass, $vhost, $exchange, $queue, $consumerTag)
{
    require_once __DIR__ . '/vendor/autoload.php';
    use PhpAmqpLib\Connection\AMQPStreamConnection;
    use PhpAmqpLib\Exchange\AMQPExchangeType;
    use PhpAmqpLib\Message\AMQPMessage;

    $connection = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    $channel = $connection->channel();

    $channel->queue_declare($queue, false, true, false, false);
    $channel->exchange_declare($exchange, AMQPExchangeType::DIRECT, false, true, false);
    $channel->queue_bind($queue, $exchange);

    function process_message($message)
    {
        echo "\n--------\n";
        echo $message->body;
        echo "\n--------\n";

        $message->ack();

        if ($message->body === 'quit') {
            $message->getChannel()->basic_cancel($message->getConsumerTag());
        }
    }

    $channel->basic_consume($queue, $consumerTag, false, false, false, false, 'process_message');

    function shutdown($channel, $connection)
    {
        $channel->close();
        $connection->close();
    }

    register_shutdown_function('shutdown', $channel, $connection);

    while ($channel->is_consuming()) {
        $channel->wait(null, true);
        usleep(300000);
    }
}

