function consume_rabbitmq_messages($headers)
{
    require __DIR__ . '/config.php';

    use PhpAmqpLib\Connection\AMQPStreamConnection;
    use PhpAmqpLib\Exception\AMQPTimeoutException;
    use PhpAmqpLib\Exchange\AMQPExchangeType;
    use PhpAmqpLib\Message\AMQPMessage;
    use PhpAmqpLib\Wire\AMQPTable;

    if (empty($headers)) {
        file_put_contents('php://stderr', "Usage: $argv[0] [header1=value1] [header2=value2]\n");
        return false;
    }

    $connection = new AMQPStreamConnection(HOST, PORT, USER, PASS, VHOST);
    $channel = $connection->channel();

    $exchangeName = 'topic_headers_test';
    $channel->exchange_declare($exchangeName, AMQPExchangeType::HEADERS);

    list($queueName, ,) = $channel->queue_declare('', false, false, true);

    $bindArguments = [];
    foreach ($headers as $header) {
        list ($key, $value) = explode('=', $header, 2);
        $bindArguments[$key] = $value;
    }

    $channel->queue_bind($queueName, $exchangeName, '', false, new AMQPTable($bindArguments));

    $callback = function (AMQPMessage $message) {
        echo PHP_EOL . ' [x] ', $message->getRoutingKey(), ':', $message->getBody(), "\n";
        echo 'Message headers follows' . PHP_EOL;
        var_dump($message->get('application_headers')->getNativeData());
        echo PHP_EOL;
    };

    $channel->basic_consume($queueName, '', false, true, true, false, $callback);
    while ($channel->is_consuming()) {
        try {
            $channel->wait(null, false, 2);
        } catch (AMQPTimeoutException $exception) {
        }
        echo '*' . PHP_EOL;
    }

    $channel->close();
    $connection->close();
    return true;
}

function send_rabbitmq_messages($max_messages, $host, $port, $user, $pass, $vhost, $message_body)
{
    use PhpAmqpLib\Connection\AMQPStreamConnection;
    use PhpAmqpLib\Message\AMQPMessage;

    $conn = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    if (!$conn) {
        return false;
    }

    $time = microtime(true);

    for ($i = 0; $i < $max_messages; $i++) {
        $ch = $conn->channel();
        list($queue,) = $ch->queue_declare('', false, false, true, true);
        $msg = new AMQPMessage($message_body);
        $ch->basic_publish($msg, '', $queue);
        $ch->close();
    }

    $execution_time = microtime(true) - $time;
    $conn->close();

    return $execution_time;
}

use GuzzleHttp\Client;

class Captcha implements Rule
{
    public function passes($attribute, $value)
    {
        if (empty($value)) {
            return false;
        }

        $response = json_decode((new Client([
            'timeout' => config('boilerplate.access.captcha.configs.options.timeout'),
        ]))->post('https://www.google.com/recaptcha/api/siteverify', [
            'form_params' => [
                'secret' => config('boilerplate.access.captcha.configs.secret_key'),
                'remoteip' => request()->getClientIp(),
                'response' => $value,
            ],
        ])->getBody(), true);

        return isset($response['success']) && $response['success'] === true;
    }

    public function message()
    {
        return __('The captcha was invalid.');
    }
}

function publish_messages($msg_count, $batch_size, $host, $port, $user, $pass, $vhost)
{
    require_once __DIR__ . '/config.php';
    use PhpAmqpLib\Connection\AMQPStreamConnection;
    use PhpAmqpLib\Exchange\AMQPExchangeType;
    use PhpAmqpLib\Message\AMQPMessage;
    use PhpAmqpLib\Exception\AMQPConnectionBlockedException;

    $exchange = 'bench_exchange';
    $queue = 'bench_queue';

    $connection = new AMQPStreamConnection($host, $port, $user, $pass, $vhost);
    $channel = $connection->channel();

    $channel->queue_declare($queue, false, false, false, false);
    $channel->exchange_declare($exchange, AMQPExchangeType::DIRECT, false, false, false);
    $channel->queue_bind($queue, $exchange);

    $messageBody = <<<EOT
abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz
abcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyzabcdefghijklmnopqrstuvwxyz
