use GuzzleHttp\Client;
use GuzzleHttp\Exception\GuzzleException;

class HttpClient
{
    private Client $client;

    public function __construct()
    {
        $this->client = new Client();
    }

    /**
     * Download remote file via HTTP
     *
     * @param string $fileName
     * @param string $savePath
     * @param string $url
     * @return bool
     */
    public function getSourceFile(string $fileName, string $savePath, string $url): bool
    {
        try {
            if (! file_exists($savePath)) {
                echo '目标文件夹 ' . $savePath . ' 不存在，下載失败。' . PHP_EOL;
                return false;
            }

            echo '- 开始下载 ' . $fileName . '...' . PHP_EOL;
            $request = $this->client->get($url);
            echo '- 下载 ' . $fileName . ' 成功，正在保存...' . PHP_EOL;
            $result = file_put_contents($savePath . $fileName, $request->getBody()->getContents());

            if (! $result) {
                echo '- 保存 ' . $fileName . ' 至 ' . $savePath . ' 失败。' . PHP_EOL;
            } else {
                echo '- 保存 ' . $fileName . ' 至 ' . $savePath . ' 成功。' . PHP_EOL;
            }

            return true;
        } catch (GuzzleException $e) {
            echo '- 下载 ' . $fileName . ' 失败...' . PHP_EOL;
            echo $e->getMessage() . PHP_EOL;

            return false;
        }
    }

    /**
     * Get latest GitHub release tag
     *
     * @param string $repo
     * @return string
     * @throws GuzzleException
     */
    public function getLatestReleaseTagName(string $repo): string
    {
        $url = 'https://api.github.com/repos/' . $repo . '/releases/latest' .
            ($_ENV['github_access_token'] !== '' ? '?access_token=' . $_ENV['github_access_token'] : '');
        $request = $this->client->get($url);

        return json_decode(
            $request->getBody()->getContents(),
            true
        )['tag_name'];
    }

    /**
     * Get latest GitHub pre-release tag
     *
     * @param string $repo
     * @return string
     * @throws GuzzleException
     */
    public function getLatestPreReleaseTagName(string $repo): string
    {
        $url = 'https://api.github.com/repos/' . $repo . '/releases' .
            ($_ENV['github_access_token'] !== '' ? '?access_token=' . $_ENV['github_access_token'] : '');
        $request = $this->client->get($url);
        $latest = json_decode(
            $request->getBody()->getContents(),
            true
        )[0];

        return $latest['tag_name'];
    }
}

namespace Think\Upload\Driver\Qiniu;

class QiniuStorage
{
    public $QINIU_RSF_HOST = 'http://rsf.qbox.me';
    public $QINIU_RS_HOST = 'http://rs.qbox.me';
    public $QINIU_UP_HOST = 'http://up.qiniu.com';
    public $timeout = '';

    private function request($path, $method, $headers = null, $body = null)
    {
        $ch = curl_init($path);

        $_headers = array('Expect:');
        if (!is_null($headers) && is_array($headers)) {
            foreach ($headers as $k => $v) {
                array_push($_headers, "{$k}: {$v}");
            }
        }

        $length = 0;
        $date = gmdate('D, d M Y H:i:s \G\M\T');

        if (!is_null($body)) {
            if (is_resource($body)) {
                fseek($body, 0, SEEK_END);
                $length = ftell($body);
                fseek($body, 0);

                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_INFILE, $body);
                curl_setopt($ch, CURLOPT_INFILESIZE, $length);
            } else {
                $length = @strlen($body);
                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
            }
        } else {
            array_push($_headers, "Content-Length: {$length}");
        }

        array_push($_headers, "Date: {$date}");

        curl_setopt($ch, CURLOPT_HTTPHEADER, $_headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->timeout);
        curl_setopt($ch, CURLOPT_HEADER, 1);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 0);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        if ('PUT' == $method || 'POST' == $method) {
            curl_setopt($ch, CURLOPT_POST, 1);
        } else {
            curl_setopt($ch, CURLOPT_POST, 0);
        }

        if ('HEAD' == $method) {
            curl_setopt($ch, CURLOPT_NOBODY, true);
        }

        $response = curl_exec($ch);
        $status = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        list($header, $body) = explode("\r\n\r\n", $response, 2);
        if (200 == $status) {
            if ('GET' == $method) {
                return $body;
            } else {
                return $this->response($response);
            }
        } else {
            $this->error($header, $body);
            return false;
        }
    }

    private function response($text)
    {
        $headers = explode(PHP_EOL, $text);
        $items = array();
        foreach ($headers as $header) {
            $header = trim($header);
            if (strpos($header, '{') !== false) {
                $items = json_decode($header, 1);
                break;
            }
        }
        return $items;
    }

    private function error($header, $body)
    {
        list($status, $stash) = explode("\r\n", $header, 2);
        list($v, $code, $message) = explode(" ", $status, 3);
        $message = is_null($message) ? 'File Not Found' : "[{$status}]:{$message}]";
        $this->error = $message;
        $this->errorStr = json_decode($body, 1);
        $this->errorStr = $this->errorStr['error'];
    }
}

class RequestCore
{
    public $request_url;
    public $request_headers;
    public $request_body;
    public $response;
    public $response_headers;
    public $response_body;
    public $response_code;
    public $response_info;
    public $curl_handle;
    public $method;
    public $proxy = null;
    public $username = null;
    public $password = null;
    public $curlopts = null;
    public $debug_mode = false;
    public $request_class = 'RequestCore';
    public $response_class = 'ResponseCore';
    public $useragent = 'RequestCore/1.4.3';
    public $read_file = null;
    public $read_stream = null;
    public $read_stream_size = null;
    public $read_stream_read = 0;
    public $write_file = null;
    public $write_stream = null;
    public $seek_position = null;
    public $cacert_location = false;
    public $ssl_verification = true;
    public $registered_streaming_read_callback = null;
    public $registered_streaming_write_callback = null;

    const HTTP_GET = 'GET';
    const HTTP_POST = 'POST';
    const HTTP_PUT = 'PUT';
    const HTTP_DELETE = 'DELETE';
    const HTTP_HEAD = 'HEAD';

    public function __construct($url = null, $proxy = null, $helpers = null)
    {
        $this->request_url = $url;
        $this->method = self::HTTP_GET;
        $this->request_headers = array();
        $this->request_body = '';

        if (isset($helpers['request']) && !empty($helpers['request']))
        {
            $this->request_class = $helpers['request'];
        }

        if (isset($helpers['response']) && !empty($helpers['response']))
        {
            $this->response_class = $helpers['response'];
        }

        if ($proxy)
        {
            $this->set_proxy($proxy);
        }

        return $this;
    }

    public function __destruct()
    {
        if (isset($this->read_file) && isset($this->read_stream))
        {
            fclose($this->read_stream);
        }

        if (isset($this->write_file) && isset($this->write_stream))
        {
            fclose($this->write_stream);
        }

        return $this;
    }

    public function set_credentials($user, $pass)
    {
        $this->username = $user;
        $this->password = $pass;
        return $this;
    }

    public function add_header($key, $value)
    {
        $this->request_headers[$key] = $value;
        return $this;
    }

    public function remove_header($key)
    {
        if (isset($this->request_headers[$key]))
        {
            unset($this->request_headers[$key]);
        }
        return $this;
    }

    public function set_method($method)
    {
        $this->method = strtoupper($method);
        return $this;
    }

    public function set_useragent($ua)
    {
        $this->useragent = $ua;
        return $this;
    }

    public function set_body($body)
    {
        $this->request_body = $body;
        return $this;
    }

    public function set_request_url($url)
    {
        $this->request_url = $url;
        return $this;
    }

    public function set_curlopts($curlopts)
    {
        $this->curlopts = $curlopts;
        return $this;
    }

    public function set_read_stream_size($size)
    {
        $this->read_stream_size = $size;
        return $this;
    }

    public function set_read_stream($resource, $size = null)
    {
        if (!isset($size) || $size < 0)
        {
            $stats = fstat($resource);

            if ($stats && $stats['size'] >= 0)
            {
                $position = ftell($resource);

                if ($position !== false && $position >= 0)
                {
                    $size = $stats['size'] - $position;
                }
            }
        }

        $this->read_stream = $resource;
        return $this->set_read_stream_size($size);
    }

    public function set_read_file($location)
    {
        $this->read_file = $location;
        $read_file_handle = fopen($location, 'r');
        return $this->set_read_stream($read_file_handle);
    }

    public function set_write_stream($resource)
    {
        $this->write_stream = $resource;
        return $this;
    }

    public function set_write_file($location)
    {
        $this->write_file = $location;
        $write_file_handle = fopen($location, 'w');
        return $this->set_write_stream($write_file_handle);
    }

    public function set_proxy($proxy)
    {
        $proxy = parse_url($proxy);
        $proxy['user'] = isset($proxy['user']) ? $proxy['user'] : null;
        $proxy['pass'] = isset($proxy['pass']) ? $proxy['pass'] : null;
        $proxy['port'] = isset($proxy['port']) ? $proxy['port'] : null;
        $this->proxy = $proxy;
        return $this;
    }

    public function set_seek_position($position)
    {
        $this->seek_position = isset($position) ? (integer) $position : null;
        return $this;
    }

    public function register_streaming_read_callback($callback)
    {
        $this->registered_streaming_read_callback = $callback;
        return $this;
    }

    public function register_streaming_write_callback($callback)
    {
        $this->registered_streaming_write_callback = $callback;
        return $this;
    }

    public function streaming_read_callback($curl_handle, $file_handle, $length)
    {
        if ($this->read_stream_read >= $this->read_stream_size)
        {
            return '';
        }

        if ($this->read_stream_read == 0 && isset($this->seek_position) && $this->seek_position !== ftell($this->read_stream))
        {
            if (fseek($this->read_stream, $this->seek_position) !== 0)
            {
                throw new RequestCore_Exception('The stream does not support seeking and is either not at the requested position or the position is unknown.');
            }
        }

        $read = fread($this->read_stream, min($this->read_stream_size - $this->read_stream_read, $length));
        $this->read_stream_read += strlen($read);

        $out = $read === false ? '' : $read;

        if ($this->registered_streaming_read_callback)
        {
            call_user_func($this->registered_streaming_read_callback, $curl_handle, $file_handle, $out);
        }

        return $out;
    }

    public function streaming_write_callback($curl_handle, $data)
    {
        $length = strlen($data);
        $written_total = 0;
        $written_last = 0;

        while ($written_total < $length)
        {
            $written_last = fwrite($this->write_stream, substr($data, $written_total));

            if ($written_last === false)
            {
                return $written_total;
            }

            $written_total += $written_last;
        }

        if ($this->registered_streaming_write_callback)
        {
            call_user_func($this->registered_streaming_write_callback, $curl_handle, $written_total);
        }

        return $written_total;
    }

    public function prep_request()
    {
        $curl_handle = curl_init();

        curl_setopt($curl_handle, CURLOPT_URL, $this->request_url);
        curl_setopt($curl_handle, CURLOPT_FILETIME, true);
        curl_setopt($curl_handle, CURLOPT_FRESH_CONNECT, false);
        curl_setopt($curl_handle, CURLOPT_CLOSEPOLICY, CURLCLOSEPOLICY_LEAST_RECENTLY_USED);
        curl_setopt($curl_handle, CURLOPT_MAXREDIRS, 5);
        curl_setopt($curl_handle, CURLOPT_HEADER, true);
        curl_setopt($curl_handle, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl_handle, CURLOPT_TIMEOUT, 5184000);
        curl_setopt($curl_handle, CURLOPT_CONNECTTIMEOUT, 120);
        curl_setopt($curl_handle, CURLOPT_NOSIGNAL, true);
        curl_setopt($curl_handle, CURLOPT_REFERER, $this->request_url);
        curl_setopt($curl_handle, CURLOPT_USERAGENT, $this->useragent);
        curl_setopt($curl_handle, CURLOPT_READFUNCTION, array($this, 'streaming_read_callback'));

        if ($this->ssl_verification)
        {
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYPEER, true);
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYHOST, true);
        }
        else
        {
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYPEER, false);
            curl_setopt($curl_handle, CURLOPT_SSL_VERIFYHOST, false);
        }

        if ($this->cacert_location === true)
        {
            curl_setopt($curl_handle, CURLOPT_CAINFO, dirname(__FILE__) . '/cacert.pem');
        }
        elseif (is_string($this->cacert_location))
        {
            curl_setopt($curl_handle, CURLOPT_CAINFO, $this->cacert_location);
        }

        if ($this->debug_mode)
        {
            curl_setopt($curl_handle, CURLOPT_VERBOSE, true);
        }

        if (!ini_get('safe_mode') && !ini_get('open_basedir'))
        {
            curl_setopt($curl_handle, CURLOPT_FOLLOWLOCATION, true);
        }

        if ($this->proxy)
        {
            curl_setopt($curl_handle, CURLOPT_HTTPPROXYTUNNEL, true);

            $host = $this->proxy['host'];
            $host .= ($this->proxy['port']) ? ':' . $this->proxy['port'] : '';
            curl_setopt($curl_handle, CURLOPT_PROXY, $host);

            if (isset($this->proxy['user']) && isset($this->proxy['pass']))
            {
                curl_setopt($curl_handle, CURLOPT_PROXYUSERPWD, $this->proxy['user'] . ':' . $this->proxy['pass']);
            }
        }

        if ($this->username && $this->password)
        {
            curl_setopt($curl_handle, CURLOPT_HTTPAUTH, CURLAUTH_ANY);
            curl_setopt($curl_handle, CURLOPT_USERPWD, $this->username . ':' . $this->password);
        }

        if (extension_loaded('zlib'))
        {
            curl_setopt($curl_handle, CURLOPT_ENCODING, '');
        }

        if (isset($this->request_headers) && count($this->request_headers))
        {
            $temp_headers = array();

            foreach ($this->request_headers as $k => $v)
            {
                $temp_headers[] = $k . ': ' . $v;
            }

            curl_setopt($curl_handle, CURLOPT_HTTPHEADER, $temp_headers);
        }

        switch ($this->method)
        {
            case self::HTTP_PUT:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, 'PUT');
                if (isset($this->read_stream))
                {
                    if (!isset($this->read_stream_size) || $this->read_stream_size < 0)
                    {
                        throw new RequestCore_Exception('The stream size for the streaming upload cannot be determined.');
                    }

                    curl_setopt($curl_handle, CURLOPT_INFILESIZE, $this->read_stream_size);
                    curl_setopt($curl_handle, CURLOPT_UPLOAD, true);
                }
                else
                {
                    curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                }
                break;

            case self::HTTP_POST:
                curl_setopt($curl_handle, CURLOPT_POST, true);
                curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                break;

            case self::HTTP_HEAD:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, self::HTTP_HEAD);
                curl_setopt($curl_handle, CURLOPT_NOBODY, 1);
                break;

            default:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, $this->method);
                if (isset($this->write_stream))
                {
                    curl_setopt($curl_handle, CURLOPT_WRITEFUNCTION, array($this, 'streaming_write_callback'));
                    curl_setopt($curl_handle, CURLOPT_HEADER, false);
                }
                else
                {
                    curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                }
                break;
        }

        if (isset($this->curlopts) && sizeof($this->curlopts) > 0)
        {
            foreach ($this->curlopts as $k => $v)
            {
                curl_setopt($curl_handle, $k, $v);
            }
        }

        return $curl_handle;
    }

    public function process_response($curl_handle = null, $response = null)
    {
        if ($curl_handle && $response)
        {
            $this->curl_handle = $curl_handle;
            $this->response = $response;
        }

        if (is_resource($this->curl_handle))
        {
            $header_size = curl_getinfo($this->curl_handle, CURLINFO_HEADER_SIZE);
            $this->response_headers = substr($this->response, 0, $header_size);
            $this->response_body = substr($this->response, $header_size);
            $this->response_code = curl_getinfo($this->curl_handle, CURLINFO_HTTP_CODE);
            $this->response_info = curl_getinfo($this->curl_handle);

            $this->response_headers = explode("\r\n\r\n", trim($this->response_headers));
            $this->response_headers = array_pop($this->response_headers);
            $this->response_headers = explode("\r\n", $this->response_headers);
            array_shift($this->response_headers);

            $header_assoc = array();
            foreach ($this->response_headers as $header)
            {
                $kv = explode(': ', $header);
                $header_assoc[strtolower($kv[0])] = isset($kv[1])?$kv[1]:'';
            }

            $this->response_headers = $header_assoc;
            $this->response_headers['_info'] = $this->response_info;
            $this->response_headers['_info']['method'] = $this->method;

            if ($curl_handle && $response)
            {
                return new $this->response_class($this->response_headers, $this->response_body, $this->response_code, $this->curl_handle);
            }
        }

        return false;
    }

    public function send_request($parse = false)
    {
        set_time_limit(0);

        $curl_handle = $this->prep_request();
        $this->response = curl_exec($curl_handle);

        if ($this->response === false)
        {
            throw new RequestCore_Exception('cURL resource: ' . (string) $curl_handle . '; cURL error: ' . curl_error($curl_handle) . ' (' . curl_errno($curl_handle) . ')');
        }

        $parsed_response = $this->process_response($curl_handle, $this->response);

        curl_close($curl_handle);

        if ($parse)
        {
            return $parsed_response;
        }

        return $this->response;
    }

    public function get_response_header($header = null)
    {
        if ($header)
        {
            return $this->response_headers[strtolower($header)];
        }
        return $this->response_headers;
    }

    public function get_response_body()
    {
        return $this->response_body;
    }

    public function get_response_code()
    {
        return $this->response_code;
    }
}

namespace Upyun\Api;

use GuzzleHttp\Client;
use Upyun\Config;
use Upyun\Signature;
use Upyun\Util;

class Pretreat
{
    protected $config;

    public function __construct(Config $config)
    {
        if (!$config->processNotifyUrl) {
            throw new \Exception("should config prosessNotifyUrl first.");
        }
        $this->config = $config;
    }

    public function process($tasks, $optionalParams = array())
    {
        $encodedTasks = Util::base64Json($tasks);

        $client = new Client([
            'timeout' => $this->config->timeout,
        ]);

        $params = array(
            'service' => $this->config->serviceName,
            'notify_url' => $this->config->processNotifyUrl,
            'tasks' => $encodedTasks,
        );

        $params = array_merge($params, $optionalParams);

        $path = '/pretreatment/';
        $method = 'POST';
        $signedHeaders = Signature::getHeaderSign($this->config, $method, $path);

        $url = $this->config->getPretreatEndPoint() . $path;
        $response = $client->request($method, $url, [
            'headers' => $signedHeaders,
            'form_params' => $params
        ]);

        $body = $response->getBody()->getContents();
        return json_decode($body, true);
    }

    public function query($taskIds, $path)
    {
        $client = new Client([
            'timeout' => $this->config->timeout,
        ]);

        $params = array(
            'service' => $this->config->serviceName,
            'task_ids' => implode(',', $taskIds)
        );
        $path = $path . '?' . http_build_query($params);

        $method = 'GET';
        $url = $this->config->getPretreatEndPoint() . $path;
        $signedHeaders = Signature::getHeaderSign($this->config, $method, $path);
        $response = $client->request($method, $url, [
            'headers' => $signedHeaders
        ]);

        if ($response->getStatusCode() === 200) {
            $body = $response->getBody()->getContents();
            $result = json_decode($body, true);
            if (is_array($result)) {
                return $result['tasks'];
            }
        }
        return false;
    }
}

use GuzzleHttp\Client;
use Exception;

class FetchConversionRate
{
    protected $baseCurrencyId;
    private $currencyRepository;
    private $conversionRateRepository;

    public function __construct(int $baseCurrencyId)
    {
        $this->baseCurrencyId = $baseCurrencyId;
    }

    public function handle(
        CurrencyRepository $currencyRepository,
        ConversionRateRepository $conversionRateRepository
    ): void {
        $this->currencyRepository = $currencyRepository;
        $this->conversionRateRepository = $conversionRateRepository;

        $baseCurrency = $this->currencyRepository->getById($this->baseCurrencyId);

        if (!$baseCurrency || !$baseCurrency->iso) {
            return;
        }

        $client = new Client();

        foreach ($this->currencyRepository->getAll() as $targetCurrency) {
            if (!$targetCurrency->iso || $baseCurrency->iso === $targetCurrency->iso) {
                continue;
            }

            $url = sprintf(
                'https://cdn.jsdelivr.net/npm/@fawazahmed0/currency-api@latest/v1/currencies/%s.json',
                $baseCurrency->iso_lowercased,
            );

            try {
                $response = $client->request('GET', $url);
                $decoded_response = json_decode($response->getBody(), true);
                $rate = $decoded_response[$baseCurrency->iso_lowercased][$targetCurrency->iso_lowercased];
            } catch (Exception) {
                continue;
            }

            $this->conversionRateRepository->createOrUpdate(
                $baseCurrency->id,
                $targetCurrency->id,
                $rate
            );
        }
    }
}

namespace Hybridauth\HttpClient;

class Curl implements HttpClientInterface
{
    protected $curlOptions = [
        CURLOPT_TIMEOUT => 30,
        CURLOPT_CONNECTTIMEOUT => 30,
        CURLOPT_SSL_VERIFYPEER => false,
        CURLOPT_SSL_VERIFYHOST => false,
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_FOLLOWLOCATION => true,
        CURLOPT_MAXREDIRS => 5,
        CURLINFO_HEADER_OUT => true,
        CURLOPT_ENCODING => 'identity',
        CURLOPT_USERAGENT => 'Hybridauth, PHP Social Authentication Library (https://github.com/hybridauth/hybridauth)',
    ];

    protected $requestArguments = [];
    protected $requestHeader = [
        'Accept' => '*/*',
        'Cache-Control' => 'max-age=0',
        'Connection' => 'keep-alive',
        'Expect' => '',
        'Pragma' => '',
    ];

    protected $responseBody = '';
    protected $responseHeader = [];
    protected $responseHttpCode = 0;
    protected $responseClientError = null;
    protected $responseClientInfo = [];
    protected $logger = null;

    public function request($uri, $method = 'GET', $parameters = [], $headers = [], $multipart = false)
    {
        $this->requestHeader = array_replace($this->requestHeader, (array)$headers);
        $this->requestArguments = [
            'uri' => $uri,
            'method' => $method,
            'parameters' => $parameters,
            'headers' => $this->requestHeader,
        ];

        $curl = curl_init();

        switch ($method) {
            case 'GET':
            case 'DELETE':
                unset($this->curlOptions[CURLOPT_POST]);
                unset($this->curlOptions[CURLOPT_POSTFIELDS]);
                $uri = $uri . (strpos($uri, '?') ? '&' : '?') . http_build_query($parameters);
                if ($method === 'DELETE') {
                    $this->curlOptions[CURLOPT_CUSTOMREQUEST] = 'DELETE';
                }
                break;
            case 'PUT':
            case 'POST':
            case 'PATCH':
                $body_content = $multipart ? $parameters : http_build_query($parameters);
                if (isset($this->requestHeader['Content-Type'])
                    && $this->requestHeader['Content-Type'] == 'application/json'
                ) {
                    $body_content = json_encode($parameters);
                }

                if ($method === 'POST') {
                    $this->curlOptions[CURLOPT_POST] = true;
                } else {
                    $this->curlOptions[CURLOPT_CUSTOMREQUEST] = $method;
                }
                $this->curlOptions[CURLOPT_POSTFIELDS] = $body_content;
                break;
        }

        $this->curlOptions[CURLOPT_URL] = $uri;
        $this->curlOptions[CURLOPT_HTTPHEADER] = $this->prepareRequestHeaders();
        $this->curlOptions[CURLOPT_HEADERFUNCTION] = [$this, 'fetchResponseHeader'];

        foreach ($this->curlOptions as $opt => $value) {
            curl_setopt($curl, $opt, $value);
        }

        $response = curl_exec($curl);

        $this->responseBody = $response;
        $this->responseHttpCode = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        $this->responseClientError = curl_error($curl);
        $this->responseClientInfo = curl_getinfo($curl);

        if ($this->logger) {
            $this->logger->debug(sprintf('%s::request( %s, %s ), response:', get_class($this), $uri, $method), $this->getResponse());
            if (false === $response) {
                $this->logger->error(sprintf('%s::request( %s, %s ), error:', get_class($this), $uri, $method), [$this->responseClientError]);
            }
        }

        curl_close($curl);
        return $this->responseBody;
    }

    public function getResponse()
    {
        $curlOptions = $this->curlOptions;
        $curlOptions[CURLOPT_HEADERFUNCTION] = '*omitted';
        return [
            'request' => $this->getRequestArguments(),
            'response' => [
                'code' => $this->getResponseHttpCode(),
                'headers' => $this->getResponseHeader(),
                'body' => $this->getResponseBody(),
            ],
            'client' => [
                'error' => $this->getResponseClientError(),
                'info' => $this->getResponseClientInfo(),
                'opts' => $curlOptions,
            ],
        ];
    }

    public function setCurlOptions($curlOptions)
    {
        foreach ($curlOptions as $opt => $value) {
            $this->curlOptions[$opt] = $value;
        }
    }

    public function setLogger($logger)
    {
        $this->logger = $logger;
    }

    public function getResponseBody()
    {
        return $this->responseBody;
    }

    public function getResponseHeader()
    {
        return $this->responseHeader;
    }

    public function getResponseHttpCode()
    {
        return $this->responseHttpCode;
    }

    public function getResponseClientError()
    {
        return $this->responseClientError;
    }

    protected function getResponseClientInfo()
    {
        return $this->responseClientInfo;
    }

    protected function getRequestArguments()
    {
        return $this->requestArguments;
    }

    protected function fetchResponseHeader($curl, $header)
    {
        $pos = strpos($header, ':');
        if (!empty($pos)) {
            $key = str_replace('-', '_', strtolower(substr($header, 0, $pos)));
            $value = trim(substr($header, $pos + 2));
            $this->responseHeader[$key] = $value;
        }
        return strlen($header);
    }

    protected function prepareRequestHeaders()
    {
        $headers = [];
        foreach ($this->requestHeader as $header => $value) {
            $headers[] = trim($header) . ': ' . trim($value);
        }
        return $headers;
    }
}

namespace phpspider\core;

class requests
{
    const VERSION = '2.0.1';
    protected static $ch = null;

    public static $timeout = 15;
    public static $encoding = null;
    public static $output_encoding = null;
    public static $cookies = array();
    public static $rawheaders = array();
    public static $domain_cookies = array();
    public static $headers = array();
    public static $useragents = array("requests/2.0.0");
    public static $client_ips = array();
    public static $proxies = array();
    public static $raw = "";
    public static $head = "";
    public static $content = "";
    public static $text = "";
    public static $info = array();
    public static $history = 302;
    public static $status_code = 0;
    public static $error = "";

    public static function init()
    {
        if (!is_resource(self::$ch))
        {
            self::$ch = curl_init();
            curl_setopt(self::$ch, CURLOPT_RETURNTRANSFER, true);
            curl_setopt(self::$ch, CURLOPT_HEADER, false);
            curl_setopt(self::$ch, CURLOPT_USERAGENT, "phpspider-requests/".self::VERSION);
            if (is_array(self::$timeout))
            {
                curl_setopt(self::$ch, CURLOPT_CONNECTTIMEOUT, self::$timeout[0]);
                curl_setopt(self::$ch, CURLOPT_TIMEOUT, self::$timeout[1]);
            }
            else
            {
                curl_setopt(self::$ch, CURLOPT_CONNECTTIMEOUT, ceil(self::$timeout / 2));
                curl_setopt(self::$ch, CURLOPT_TIMEOUT, self::$timeout);
            }
            curl_setopt(self::$ch, CURLOPT_MAXREDIRS, 5);
            curl_setopt(self::$ch, CURLOPT_NOSIGNAL, true);
        }
        return self::$ch;
    }

    public static function request($url, $method = 'GET', $fields = array(), $files = array(), $allow_redirects = true, $cert = NULL)
    {
        $method = strtoupper($method);
        if(!self::_is_url($url))
        {
            self::$error = "You have requested URL ({$url}) is not a valid HTTP address";
            return false;
        }

        if ($method == 'GET' && !empty($fields))
        {
            $url = $url.(strpos($url, '?') === false ? '?' : '&').http_build_query($fields);
        }

        $parse_url = parse_url($url);
        if (empty($parse_url) || empty($parse_url['host']) || !in_array($parse_url['scheme'], array('http', 'https')))
        {
            self::$error = "No connection adapters were found for '{$url}'";
            return false;
        }

        curl_setopt(self::$ch, CURLOPT_URL, $url);

        if ($method != 'GET')
        {
            if ($method == 'POST')
            {
                $tmpheaders = array_change_key_case(self::$rawheaders, CASE_LOWER);
                if (isset($tmpheaders['content-type']) && $tmpheaders['content-type'] == 'application/json')
                {
                    curl_setopt(self::$ch, CURLOPT_CUSTOMREQUEST, $method);
                }
                else
                {
                    curl_setopt(self::$ch, CURLOPT_POST, true);
                }

                $file_fields = array();
                if (!empty($files))
                {
                    foreach ($files as $postname => $file)
                    {
                        $filepath = realpath($file);
                        if (!file_exists($filepath))
                        {
                            continue;
                        }
                        $filename = basename($filepath);
                        $type = self::get_mimetype($filepath);
                        $file_fields[$postname] = curl_file_create($filepath, $type, $filename);
                    }
                }

                if (empty($file_fields))
                {
                    if (is_array($fields))
                    {
                        $fields = http_build_query($fields);
                    }
                }
                else
                {
                    if (is_array($fields) && !empty($fields))
                    {
                        $fields = array_merge($fields, $file_fields);
                    }
                    else
                    {
                        $fields = $file_fields;
                    }
                }
                curl_setopt(self::$ch, CURLOPT_POSTFIELDS, $fields);
            }
            else
            {
                self::$rawheaders['X-HTTP-Method-Override'] = $method;
                curl_setopt(self::$ch, CURLOPT_CUSTOMREQUEST, $method);
            }
        }

        $cookies = self::get_cookies();
        $domain_cookies = self::get_cookies($parse_url['host']);
        $cookies = array_merge($cookies, $domain_cookies);
        if (!empty($cookies))
        {
            foreach ($cookies as $key=>$value)
            {
                $cookie_arr[] = $key.'='.$value;
            }
            $cookies = implode('; ', $cookie_arr);
            curl_setopt(self::$ch, CURLOPT_COOKIE, $cookies);
        }

        if (!empty(self::$useragents))
        {
            $key = rand(0, count(self::$useragents) - 1);
            self::$rawheaders['User-Agent'] = self::$useragents[$key];
        }

        if (!empty(self::$client_ips))
        {
            $key = rand(0, count(self::$client_ips) - 1);
            self::$rawheaders['CLIENT-IP'] = self::$client_ips[$key];
            self::$rawheaders['X-FORWARDED-FOR'] = self::$client_ips[$key];
        }

        if (self::$rawheaders)
        {
            $http_headers = array();
            foreach (self::$rawheaders as $k=>$v)
            {
                $http_headers[] = $k.': '.$v;
            }
            curl_setopt(self::$ch, CURLOPT_HTTPHEADER, $http_headers);
        }

        curl_setopt(self::$ch, CURLOPT_ENCODING, 'gzip');

        if ($parse_url['scheme'] == 'https')
        {
            curl_setopt(self::$ch, CURLOPT_SSL_VERIFYPEER, false);
            curl_setopt(self::$ch, CURLOPT_SSL_VERIFYHOST, false);
        }

        if (self::$proxies)
        {
            $key = rand(0, count(self::$proxies) - 1);
            $proxy = self::$proxies[$key];
            curl_setopt(self::$ch, CURLOPT_PROXY, $proxy);
        }

        curl_setopt(self::$ch, CURLOPT_HEADER, true);
        if ($allow_redirects)
        {
            curl_setopt(self::$ch, CURLOPT_FOLLOWLOCATION, true);
        }

        self::$raw = curl_exec(self::$ch);
        self::$info = curl_getinfo(self::$ch);
        self::$status_code = self::$info['http_code'];
        if (self::$raw === false)
        {
            self::$error = 'Curl error: ' . curl_error(self::$ch);
        }

        curl_close(self::$ch);

        list($header, $text) = self::split_header_body();
        self::$history = self::get_history($header);
        self::$headers = self::get_response_headers($header);
        self::get_response_cookies($header, $parse_url['host']);
        return $text;
    }

    private static function _is_url($url)
    {
        $pattern = "/\b(([\w-]+:\/\/?|www[.])[^\s()<>]+(?:\([\w\d]+\)|([^[:punct:]\s]|\/)))/";
        return preg_match($pattern, $url) ? true : false;
    }
}

use GuzzleHttp\Client;
use GuzzleHttp\Psr7\Request;
use Psr\Http\Client\ClientExceptionInterface;
use Psr\Http\Client\ClientInterface;
use Psr\Http\Message\RequestInterface;

class Dom implements DomInterface
{
    /**
     * Use a curl interface implementation to attempt to load
     * the content from a url.
     *
     * @throws ChildNotFoundException
     * @throws CircularException
     * @throws Exceptions\ContentLengthException
     * @throws LogicalException
     * @throws StrictException
     * @throws ClientExceptionInterface
     */
    public function loadFromUrl(string $url, ?Options $options = null, ?ClientInterface $client = null, ?RequestInterface $request = null): Dom
    {
        if ($client === null) {
            $client = new Client();
        }
        if ($request === null) {
            $request = new Request('GET', $url);
        }

        $response = $client->sendRequest($request);
        $content = $response->getBody()->getContents();

        return $this->loadStr($content, $options);
    }
}
