namespace App\Widgets\Picture;

use Movim\Image;

class Picture
{
    private $compressLimit = SMALL_PICTURE_LIMIT * 6;
    private $sizeLimit = 1920;

    public function downloadImage($url)
    {
        $headers = requestHeaders($url);
        $chunks = '';

        $max = $headers["download_content_length"] > $this->compressLimit ? $this->compressLimit : $headers["download_content_length"];

        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 10);
        curl_setopt($ch, CURLOPT_RANGE, '0-' . $max);
        curl_setopt($ch, CURLOPT_BUFFERSIZE, 12800);
        curl_setopt($ch, CURLOPT_NOPROGRESS, false);
        curl_setopt($ch, CURLOPT_USERAGENT, DEFAULT_HTTP_USER_AGENT);
        curl_setopt($ch, CURLOPT_WRITEFUNCTION, function ($ch, $chunk) use (&$chunks) {
            $chunks .= $chunk;
            return (strlen($chunks) >= $this->compressLimit + 1) ? 0 : strlen($chunk);
        });

        curl_exec($ch);

        $headerSize = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        curl_close($ch);

        $headers = preg_split('/[\r\n]+/', substr($chunks, 0, $headerSize));
        $body = substr($chunks, $headerSize);

        return $body;
    }
}

function smarty_function_fetch($params, $template)
{
    if (empty($params['file'])) {
        trigger_error("[plugin] fetch parameter 'file' cannot be empty", E_USER_NOTICE);
        return;
    }

    $content = '';
    if (preg_match('!^http://!i', $params['file'])) {
        if ($uri_parts = parse_url($params['file'])) {
            $host = $server_name = $uri_parts['host'];
            $timeout = 30;
            $port = empty($uri_parts['port']) ? 80 : $uri_parts['port'];
            $user = empty($uri_parts['user']) ? null : $uri_parts['user'];
            $pass = empty($uri_parts['pass']) ? null : $uri_parts['pass'];
            $uri = !empty($uri_parts['path']) ? $uri_parts['path'] : '/';
            $uri .= !empty($uri_parts['query']) ? '?' . $uri_parts['query'] : '';

            $_is_proxy = false;
            foreach ($params as $param_key => $param_value) {
                switch ($param_key) {
                    case "proxy_host":
                        $proxy_host = $param_value;
                        break;
                    case "proxy_port":
                        $proxy_port = (int)$param_value;
                        break;
                    case "timeout":
                        $timeout = (int)$param_value;
                        break;
                }
            }

            if (!empty($proxy_host) && !empty($proxy_port)) {
                $_is_proxy = true;
                $fp = fsockopen($proxy_host, $proxy_port, $errno, $errstr, $timeout);
            } else {
                $fp = fsockopen($server_name, $port, $errno, $errstr, $timeout);
            }

            if (!$fp) {
                trigger_error("[plugin] unable to fetch: $errstr ($errno)", E_USER_NOTICE);
                return;
            }

            if ($_is_proxy) {
                fputs($fp, 'GET ' . $params['file'] . " HTTP/1.0\r\n");
            } else {
                fputs($fp, "GET $uri HTTP/1.0\r\n");
            }
            fputs($fp, "Host: $host\r\n");
            if (!empty($user) && !empty($pass)) {
                fputs($fp, "Authorization: BASIC " . base64_encode("$user:$pass") . "\r\n");
            }
            fputs($fp, "\r\n");

            while (!feof($fp)) {
                $content .= fgets($fp, 4096);
            }
            fclose($fp);

            $csplit = preg_split("!\r\n\r\n!", $content, 2);
            $content = $csplit[1];
        }
    }

    if (!empty($params['assign'])) {
        $template->assign($params['assign'], $content);
    } else {
        return $content;
    }
}

function smarty_function_fetch($params, $template)
{
    if (empty($params['file'])) {
        trigger_error("[plugin] fetch parameter 'file' cannot be empty",E_USER_NOTICE);
        return;
    }

    if (stripos($params['file'], 'file://') === 0) {
        $params['file'] = substr($params['file'], 7);
    }

    $protocol = strpos($params['file'], '://');
    if ($protocol !== false) {
        $protocol = strtolower(substr($params['file'], 0, $protocol));
    }

    if (isset($template->smarty->security_policy)) {
        if ($protocol) {
            if (!$template->smarty->security_policy->isTrustedUri($params['file'])) {
                return;
            }
        } else {
            if (!$template->smarty->security_policy->isTrustedResourceDir($params['file'])) {
                return;
            }
        }
    }

    $content = '';
    if ($protocol == 'http') {
        if ($uri_parts = parse_url($params['file'])) {
            $host = $server_name = $uri_parts['host'];
            $timeout = 30;
            $port = empty($uri_parts['port']) ? 80 : $uri_parts['port'];
            $user = !empty($uri_parts['user']) ? $uri_parts['user'] : '';
            $pass = !empty($uri_parts['pass']) ? $uri_parts['pass'] : '';
            $uri = !empty($uri_parts['path']) ? $uri_parts['path'] : '/';
            $uri .= !empty($uri_parts['query']) ? '?' . $uri_parts['query'] : '';
            $_is_proxy = false;

            foreach ($params as $param_key => $param_value) {
                switch ($param_key) {
                    case "file":
                    case "assign":
                    case "assign_headers":
                        break;
                    case "user":
                        if (!empty($param_value)) $user = $param_value;
                        break;
                    case "pass":
                        if (!empty($param_value)) $pass = $param_value;
                        break;
                    case "proxy_host":
                        if (!empty($param_value)) $proxy_host = $param_value;
                        break;
                    case "proxy_port":
                        if (!preg_match('!\D!', $param_value)) {
                            $proxy_port = (int) $param_value;
                        }
                        break;
                    default:
                        break;
                }
            }

            if (!empty($proxy_host) && !empty($proxy_port)) {
                $_is_proxy = true;
                $fp = fsockopen($proxy_host,$proxy_port,$errno,$errstr,$timeout);
            } else {
                $fp = fsockopen($server_name,$port,$errno,$errstr,$timeout);
            }

            if (!$fp) {
                trigger_error("[plugin] unable to fetch: $errstr ($errno)",E_USER_NOTICE);
                return;
            } else {
                if ($_is_proxy) {
                    fputs($fp, 'GET ' . $params['file'] . " HTTP/1.0\r\n");
                } else {
                    fputs($fp, "GET $uri HTTP/1.0\r\n");
                }
                fputs($fp, "Host: $host\r\n");
                if (!empty($user) && !empty($pass)) {
                    fputs($fp, "Authorization: BASIC ".base64_encode("$user:$pass")."\r\n");
                }
                fputs($fp, "\r\n");

                while (!feof($fp)) {
                    $content .= fgets($fp,4096);
                }
                fclose($fp);
                $csplit = preg_split("!\r\n\r\n!",$content,2);
                $content = $csplit[1];
            }
        } else {
            trigger_error("[plugin fetch] unable to parse URL, check syntax",E_USER_NOTICE);
            return;
        }
    } else {
        $content = @file_get_contents($params['file']);
    }

    if (!empty($params['assign'])) {
        $template->assign($params['assign'], $content);
    } else {
        return $content;
    }
}

class CI_Trackback {
    public $charset = 'UTF-8';
    public $data = array(
        'url' => '',
        'title' => '',
        'excerpt' => '',
        'blog_name' => '',
        'charset' => ''
    );
    public $convert_ascii = TRUE;
    public $response = '';
    public $error_msg = array();

    public function process($url, $data) {
        $target = parse_url($url);

        if ( ! $fp = @fsockopen($target['host'], 80)) {
            $this->set_error('Invalid Connection: '.$url);
            return FALSE;
        }

        $path = isset($target['path']) ? $target['path'] : $url;
        empty($target['query']) OR $path .= '?'.$target['query'];

        if ($id = $this->get_id($url)) {
            $data = 'tb_id='.$id.'&'.$data;
        }

        fputs($fp, 'POST '.$path." HTTP/1.0\r\n");
        fputs($fp, 'Host: '.$target['host']."\r\n");
        fputs($fp, "Content-type: application/x-www-form-urlencoded\r\n");
        fputs($fp, 'Content-length: '.strlen($data)."\r\n");
        fputs($fp, "Connection: close\r\n\r\n");
        fputs($fp, $data);

        $this->response = '';
        while ( ! feof($fp)) {
            $this->response .= fgets($fp, 128);
        }
        @fclose($fp);

        if (stripos($this->response, '<error>0</error>') === FALSE) {
            $message = preg_match('/<message>(.*?)<\/message>/is', $this->response, $match)
                ? trim($match[1])
                : 'An unknown error was encountered';
            $this->set_error($message);
            return FALSE;
        }

        return TRUE;
    }

    public function get_id($url) {
        $tb_id = '';

        if (strpos($url, '?') !== FALSE) {
            $tb_array = explode('/', $url);
            $tb_end   = $tb_array[count($tb_array)-1];

            if ( ! is_numeric($tb_end)) {
                $tb_end  = $tb_array[count($tb_array)-2];
            }

            $tb_array = explode('=', $tb_end);
            $tb_id    = $tb_array[count($tb_array)-1];
        }
        else {
            $url = rtrim($url, '/');

            $tb_array = explode('/', $url);
            $tb_id    = $tb_array[count($tb_array)-1];

            if ( ! is_numeric($tb_id)) {
                $tb_id = $tb_array[count($tb_array)-2];
            }
        }

        return ctype_digit((string) $tb_id) ? $tb_id : FALSE;
    }

    public function set_error($msg) {
        $this->error_msg[] = $msg;
    }
}

class soap_transport_http extends nusoap_base {
    var $url = '';
    var $uri = '';
    var $digest_uri = '';
    var $scheme = '';
    var $host = '';
    var $port = '';
    var $path = '';
    var $request_method = 'POST';
    var $protocol_version = '1.0';
    var $encoding = '';
    var $outgoing_headers = array();
    var $incoming_headers = array();
    var $incoming_cookies = array();
    var $outgoing_payload = '';
    var $incoming_payload = '';
    var $response_status_line;
    var $useSOAPAction = true;
    var $persistentConnection = false;
    var $ch = false;
    var $ch_options = array();
    var $use_curl = false;
    var $proxy = null;
    var $username = '';
    var $password = '';
    var $authtype = '';
    var $digestRequest = array();
    var $certRequest = array();

    function soap_transport_http($url, $curl_options = NULL, $use_curl = false) {
        parent::nusoap_base();
        $this->debug("ctor url=$url use_curl=$use_curl curl_options:");
        $this->appendDebug($this->varDump($curl_options));
        $this->setURL($url);
        if (is_array($curl_options)) {
            $this->ch_options = $curl_options;
        }
        $this->use_curl = $use_curl;
        preg_match('/\$Revisio' . 'n: ([^ ]+)/', $this->revision, $rev);
        $this->setHeader('User-Agent', $this->title.'/'.$this->version.' ('.$rev[1].')');
    }

    function connect($connection_timeout=0,$response_timeout=30) {
        $this->debug("connect connection_timeout $connection_timeout, response_timeout $response_timeout, scheme $this->scheme, host $this->host, port $this->port");
        if ($this->io_method() == 'socket') {
            if (!is_array($this->proxy)) {
                $host = $this->host;
                $port = $this->port;
            } else {
                $host = $this->proxy['host'];
                $port = $this->proxy['port'];
            }

            if($this->persistentConnection && isset($this->fp) && is_resource($this->fp)){
                if (!feof($this->fp)) {
                    $this->debug('Re-use persistent connection');
                    return true;
                }
                fclose($this->fp);
                $this->debug('Closed persistent connection at EOF');
            }

            if ($this->scheme == 'ssl') {
                $host = 'ssl://' . $host;
            }
            $this->debug('calling fsockopen with host ' . $host . ' connection_timeout ' . $connection_timeout);

            if($connection_timeout > 0){
                $this->fp = @fsockopen( $host, $this->port, $this->errno, $this->error_str, $connection_timeout);
            } else {
                $this->fp = @fsockopen( $host, $this->port, $this->errno, $this->error_str);
            }

            if(!$this->fp) {
                $msg = 'Couldn\'t open socket connection to server ' . $this->url;
                if ($this->errno) {
                    $msg .= ', Error ('.$this->errno.'): '.$this->error_str;
                } else {
                    $msg .= ' prior to connect().  This is often a problem looking up the host name.';
                }
                $this->debug($msg);
                $this->setError($msg);
                return false;
            }

            $this->debug('set response timeout to ' . $response_timeout);
            socket_set_timeout( $this->fp, $response_timeout);

            $this->debug('socket connected');
            return true;
        } else if ($this->io_method() == 'curl') {
            if (!extension_loaded('curl')) {
                $this->setError('The PHP cURL Extension is required for HTTPS or NLTM.  You will need to re-build or update your PHP to include cURL or change php.ini to load the PHP cURL extension.');
                return false;
            }
            $this->debug('connect using cURL');
            $this->ch = curl_init();
            $hostURL = ($this->port != '') ? "$this->scheme://$this->host:$this->port" : "$this->scheme://$this->host";
            $hostURL .= $this->path;
            $this->setCurlOption(CURLOPT_URL, $hostURL);
            if (ini_get('safe_mode') || ini_get('open_basedir')) {
                $this->debug('safe_mode or open_basedir set, so do not set CURLOPT_FOLLOWLOCATION');
            } else {
                $this->setCurlOption(CURLOPT_FOLLOWLOCATION, 1);
            }
            $this->setCurlOption(CURLOPT_HEADER, 1);
            $this->setCurlOption(CURLOPT_RETURNTRANSFER, 1);
            if ($this->persistentConnection) {
                $this->persistentConnection = false;
                $this->setHeader('Connection', 'close');
            }
            if ($connection_timeout != 0) {
                $this->setCurlOption(CURLOPT_CONNECTIONTIMEOUT, $connection_timeout);
            }
            if ($response_timeout != 0) {
                $this->setCurlOption(CURLOPT_TIMEOUT, $response_timeout);
            }
            $this->debug('cURL connection set up');
            return true;
        } else {
            $this->setError('Unknown scheme ' . $this->scheme);
            $this->debug('Unknown scheme ' . $this->scheme);
            return false;
        }
    }

    function send($data, $timeout=0, $response_timeout=30, $cookies=NULL) {
        $this->debug('entered send() with data of length: '.strlen($data));
        $this->tryagain = true;
        $tries = 0;
        while ($this->tryagain) {
            $this->tryagain = false;
            if ($tries++ < 2) {
                if (!$this->connect($timeout, $response_timeout)){
                    return false;
                }
                if (!$this->sendRequest($data, $cookies)){
                    return false;
                }
                $respdata = $this->getResponse();
            } else {
                $this->setError("Too many tries to get an OK response ($this->response_status_line)");
            }
        }        
        $this->debug('end of send()');
        return $respdata;
    }

    function sendRequest($data, $cookies = NULL) {
        $cookie_str = $this->getCookiesForRequest($cookies, (($this->scheme == 'ssl') || ($this->scheme == 'https')));
        $this->buildPayload($data, $cookie_str);
        if ($this->io_method() == 'socket') {
            if(!fputs($this->fp, $this->outgoing_payload, strlen($this->outgoing_payload))) {
                $this->setError('couldn\'t write message data to socket');
                $this->debug('couldn\'t write message data to socket');
                return false;
            }
            $this->debug('wrote data to socket, length = ' . strlen($this->outgoing_payload));
            return true;
        } else if ($this->io_method() == 'curl') {
            $curl_headers = array();
            foreach($this->outgoing_headers as $k => $v){
                if ($k == 'Connection' || $k == 'Content-Length' || $k == 'Host' || $k == 'Authorization' || $k == 'Proxy-Authorization') {
                    $this->debug("Skip cURL header $k: $v");
                } else {
                    $curl_headers[] = "$k: $v";
                }
            }
            if ($cookie_str != '') {
                $curl_headers[] = 'Cookie: ' . $cookie_str;
            }
            $this->setCurlOption(CURLOPT_HTTPHEADER, $curl_headers);
            $this->debug('set cURL HTTP headers');
            if ($this->request_method == "POST") {
                $this->setCurlOption(CURLOPT_POST, 1);
                $this->setCurlOption(CURLOPT_POSTFIELDS, $data);
                $this->debug('set cURL POST data');
            }
            foreach ($this->ch_options as $key => $val) {
                $this->setCurlOption($key, $val);
            }
            $this->debug('set cURL payload');
            return true;
        }
    }

    function getResponse(){
        $this->incoming_payload = '';
        if ($this->io_method() == 'socket') {
            $data = '';
            while (!isset($lb)){
                if(feof($this->fp)) {
                    $this->incoming_payload = $data;
                    $this->debug('found no headers before EOF after length ' . strlen($data));
                    $this->debug("received before EOF:\n" . $data);
                    $this->setError('server failed to send headers');
                    return false;
                }
                $tmp = fgets($this->fp, 256);
                $tmplen = strlen($tmp);
                $this->debug("read line of $tmplen bytes: " . trim($tmp));
                if ($tmplen == 0) {
                    $this->incoming_payload = $data;
                    $this->debug('socket read of headers timed out after length ' . strlen($data));
                    $this->debug("read before timeout: " . $data);
                    $this->setError('socket read of headers timed out');
                    return false;
                }
                $data .= $tmp;
                $pos = strpos($data,"\r\n\r\n");
                if($pos > 1){
                    $lb = "\r\n";
                } else {
                    $pos = strpos($data,"\n\n");
                    if($pos > 1){
                        $lb = "\n";
                    }
                }
                if (isset($lb) && preg_match('/^HTTP\/1.1 100/',$data)) {
                    unset($lb);
                    $data = '';
                }
            }
            $this->incoming_payload .= $data;
            $this->debug('found end of headers after length ' . strlen($data));
            $header_data = trim(substr($data,0,$pos));
            $header_array = explode($lb,$header_data);
            $this->incoming_headers = array();
            $this->incoming_cookies = array();
            foreach($header_array as $header_line){
                $arr = explode(':',$header_line, 2);
                if(count($arr) > 1){
                    $header_name = strtolower(trim($arr[0]));
                    $this->incoming_headers[$header_name] = trim($arr[1]);
                    if ($header_name == 'set-cookie') {
                        $cookie = $this->parseCookie(trim($arr[1]));
                        if ($cookie) {
                            $this->incoming_cookies[] = $cookie;
                            $this->debug('found cookie: ' . $cookie['name'] . ' = ' . $cookie['value']);
                        } else {
                            $this->debug('did not find cookie in ' . trim($arr[1]));
                        }
                    }
                } else if (isset($header_name)) {
                    $this->incoming_headers[$header_name] .= $lb . ' ' . $header_line;
                }
            }
            if (isset($this->incoming_headers['transfer-encoding']) && strtolower($this->incoming_headers['transfer-encoding']) == 'chunked') {
                $content_length =  2147483647;
                $chunked = true;
                $this->debug("want to read chunked content");
            } elseif (isset($this->incoming_headers['content-length'])) {
                $content_length = $this->incoming_headers['content-length'];
                $chunked = false;
                $this->debug("want to read content of length $content_length");
            } else {
                $content_length =  2147483647;
                $chunked = false;
                $this->debug("want to read content to EOF");
            }
            $data = '';
            do {
                if ($chunked) {
                    $tmp = fgets($this->fp, 256);
                    $tmplen = strlen($tmp);
                    $this->debug("read chunk line of $tmplen bytes");
                    if ($tmplen == 0) {
                        $this->incoming_payload = $data;
                        $this->debug('socket read of chunk length timed out after length ' . strlen($data));
                        $this->debug("read before timeout:\n" . $data);
                        $this->setError('socket read of chunk length timed out');
                        return false;
                    }
                    $content_length = hexdec(trim($tmp));
                    $this->debug("chunk length $content_length");
                }
                $strlen = 0;
                while (($strlen < $content_length) && (!feof($this->fp))) {
                    $readlen = min(8192, $content_length - $strlen);
                    $tmp = fread($this->fp, $readlen);
                    $tmplen = strlen($tmp);
                    $this->debug("read buffer of $tmplen bytes");
                    if (($tmplen == 0) && (!feof($this->fp))) {
                        $this->incoming_payload = $data;
                        $this->debug('socket read of body timed out after length ' . strlen($data));
                        $this->debug("read before timeout:\n" . $data);
                        $this->setError('socket read of body timed out');
                        return false;
                    }
                    $strlen += $tmplen;
                    $data .= $tmp;
                }
                if ($chunked && ($content_length > 0)) {
                    $tmp = fgets($this->fp, 256);
                    $tmplen = strlen($tmp);
                    $this->debug("read chunk terminator of $tmplen bytes");
                    if ($tmplen == 0) {
                        $this->incoming_payload = $data;
                        $this->debug('socket read of chunk terminator timed out after length ' . strlen($data));
                        $this->debug("read before timeout:\n" . $data);
                        $this->setError('socket read of chunk terminator timed out');
                        return false;
                    }
                }
            } while ($chunked && ($content_length > 0) && (!feof($this->fp)));
            if (feof($this->fp)) {
                $this->debug('read to EOF');
            }
            $this->debug('read body of length ' . strlen($data));
            $this->incoming_payload .= $data;
            $this->debug('received a total of '.strlen($this->incoming_payload).' bytes of data from server');
            if(
                (isset($this->incoming_headers['connection']) && strtolower($this->incoming_headers['connection']) == 'close') || 
                (! $this->persistentConnection) || feof($this->fp)){
                fclose($this->fp);
                $this->fp = false;
                $this->debug('closed socket');
            }
            if($this->incoming_payload == ''){
                $this->setError('no response from server');
                return false;
            }
            return $data;
        } else if ($this->io_method() == 'curl') {
            $this->debug('send and receive with cURL');
            $this->incoming_payload = curl_exec($this->ch);
            $data = $this->incoming_payload;
            $cErr = curl_error($this->ch);
            if ($cErr != '') {
                $err = 'cURL ERROR: '.curl_errno($this->ch).': '.$cErr.'<br>';
                foreach(curl_getinfo($this->ch) as $k => $v){
                    $err .= "$k: $v<br>";
                }
                $this->debug($err);
                $this->setError($err);
                curl_close($this->ch);
                return false;
            }
            $this->debug('No cURL error, closing cURL');
            curl_close($this->ch);
            $savedata = $data;
            while ($this->isSkippableCurlHeader($data)) {
                $this->debug("Found HTTP header to skip");
                if ($pos = strpos($data,"\r\n\r\n")) {
                    $data = ltrim(substr($data,$pos));
                } elseif($pos = strpos($data,"\n\n") ) {
                    $data = ltrim(substr($data,$pos));
                }
            }
            if ($data == '') {
                $data = $savedata;
                while (preg_match('/^HTTP\/1.1 100/',$data)) {
                    if ($pos = strpos($data,"\r\n\r\n")) {
                        $data = ltrim(substr($data,$pos));
                    } elseif($pos = strpos($data,"\n\n") ) {
                        $data = ltrim(substr($data,$pos));
                    }
                }
            }
            if ($pos = strpos($data,"\r\n\r\n")) {
                $lb = "\r\n";
            } elseif( $pos = strpos($data,"\n\n")) {
                $lb = "\n";
            } else {
                $this->debug('no proper separation of headers and document');
                $this->setError('no proper separation of headers and document');
                return false;
            }
