class RabbitMQConsumer
{
    protected $msgCount = 0;
    protected $startTime = null;
    protected $conn;
    protected $ch;
    protected $exchange = 'bench_exchange';
    protected $queue = 'bench_queue';
    protected $consumer_tag = '';

    public function __construct()
    {
        $this->conn = new AMQPStreamConnection(HOST, PORT, USER, PASS, VHOST);
        $this->ch = $this->conn->channel();
        $this->ch->queue_declare($this->queue, false, false, false, false);
        $this->ch->exchange_declare($this->exchange, 'direct', false, false, false);
        $this->ch->queue_bind($this->queue, $this->exchange);
    }

    public function process_message($msg)
    {
        if ($this->startTime === null) {
            $this->startTime = microtime(true);
        }

        if ($msg->body == 'quit') {
            echo sprintf("Pid: %s, Count: %s, Time: %.4f\n", getmypid(), $this->msgCount, microtime(true) - $this->startTime);
            $this->shutdown();
            die;
        }
        $this->msgCount++;
    }

    public function startConsuming()
    {
        $this->ch->basic_consume($this->queue, '', false, true, false, false, array($this, 'process_message'));
        while ($this->ch->is_consuming()) {
            $this->ch->wait();
        }
    }

    protected function shutdown()
    {
        $this->ch->close();
        $this->conn->close();
    }
}

function send_delayed_message($message_content, $delay_ms)
{
    require_once __DIR__ . '/config.php';
    use PhpAmqpLib\Connection\AMQPStreamConnection;
    use PhpAmqpLib\Exchange\AMQPExchangeType;
    use PhpAmqpLib\Message\AMQPMessage;
    use PhpAmqpLib\Wire\AMQPTable;

    $connection = new AMQPStreamConnection(HOST, PORT, USER, PASS, VHOST);
    $channel = $connection->channel();

    $channel->exchange_declare(
        'delayed_exchange',
        'x-delayed-message',
        false,
        true,
        false,
        false,
        false,
        new AMQPTable(['x-delayed-type' => AMQPExchangeType::FANOUT])
    );

    $channel->queue_declare(
        'delayed_queue',
        false,
        false,
        false,
        false,
        false,
        new AMQPTable(['x-dead-letter-exchange' => 'delayed'])
    );

    $channel->queue_bind('delayed_queue', 'delayed_exchange');

    $headers = new AMQPTable(['x-delay' => $delay_ms]);
    $message = new AMQPMessage($message_content, ['delivery_mode' => 2]);
    $message->set('application_headers', $headers);
    $channel->basic_publish($message, 'delayed_exchange');

    $channel->close();
    $connection->close();
}

