class CI_Session_redis_driver extends CI_Session_driver implements SessionHandlerInterface {

    protected $_redis;
    protected $_key_prefix = 'ci_session:';
    protected $_lock_key;
    protected $_key_exists = FALSE;

    public function __construct(&$params)
    {
        parent::__construct($params);

        if (empty($this->_config['save_path']))
        {
            log_message('error', 'Session: No Redis save path configured.');
        }
        elseif (preg_match('#(?:tcp://)?([^:?]+)(?:\:(\d+))?(\?.+)?#', $this->_config['save_path'], $matches))
        {
            isset($matches[3]) OR $matches[3] = '';
            $this->_config['save_path'] = array(
                'host' => $matches[1],
                'port' => empty($matches[2]) ? NULL : $matches[2],
                'password' => preg_match('#auth=([^\s&]+)#', $matches[3], $match) ? $match[1] : NULL,
                'database' => preg_match('#database=(\d+)#', $matches[3], $match) ? (int) $match[1] : NULL,
                'timeout' => preg_match('#timeout=(\d+\.\d+)#', $matches[3], $match) ? (float) $match[1] : NULL
            );

            preg_match('#prefix=([^\s&]+)#', $matches[3], $match) && $this->_key_prefix = $match[1];
        }
        else
        {
            log_message('error', 'Session: Invalid Redis save path format: '.$this->_config['save_path']);
        }

        if ($this->_config['match_ip'] === TRUE)
        {
            $this->_key_prefix .= $_SERVER['REMOTE_ADDR'].':';
        }
    }

    public function open($save_path, $name)
    {
        if (empty($this->_config['save_path']))
        {
            return $this->_fail();
        }

        $redis = new Redis();
        if ( ! $redis->connect($this->_config['save_path']['host'], $this->_config['save_path']['port'], $this->_config['save_path']['timeout']))
        {
            log_message('error', 'Session: Unable to connect to Redis with the configured settings.');
        }
        elseif (isset($this->_config['save_path']['password']) && ! $redis->auth($this->_config['save_path']['password']))
        {
            log_message('error', 'Session: Unable to authenticate to Redis instance.');
        }
        elseif (isset($this->_config['save_path']['database']) && ! $redis->select($this->_config['save_path']['database']))
        {
            log_message('error', 'Session: Unable to select Redis database with index '.$this->_config['save_path']['database']);
        }
        else
        {
            $this->_redis = $redis;
            return $this->_success;
        }

        return $this->_fail();
    }

    public function read($session_id)
    {
        if (isset($this->_redis) && $this->_get_lock($session_id))
        {
            $this->_session_id = $session_id;

            $session_data = $this->_redis->get($this->_key_prefix.$session_id);

            is_string($session_data)
                ? $this->_key_exists = TRUE
                : $session_data = '';

            $this->_fingerprint = md5($session_data);
            return $session_data;
        }

        return $this->_fail();
    }

    public function write($session_id, $session_data)
    {
        if ( ! isset($this->_redis, $this->_lock_key))
        {
            return $this->_fail();
        }
        elseif ($session_id !== $this->_session_id)
        {
            if ( ! $this->_release_lock() OR ! $this->_get_lock($session_id))
            {
                return $this->_fail();
            }

            $this->_key_exists = FALSE;
            $this->_session_id = $session_id;
        }

        $this->_redis->setTimeout($this->_lock_key, 300);
        if ($this->_fingerprint !== ($fingerprint = md5($session_data)) OR $this->_key_exists === FALSE)
        {
            if ($this->_redis->set($this->_key_prefix.$session_id, $session_data, $this->_config['expiration']))
            {
                $this->_fingerprint = $fingerprint;
                $this->_key_exists = TRUE;
                return $this->_success;
            }

            return $this->_fail();
        }

        return ($this->_redis->setTimeout($this->_key_prefix.$session_id, $this->_config['expiration']))
            ? $this->_success
            : $this->_fail();
    }

    public function close()
    {
        if (isset($this->_redis))
        {
            try {
                if ($this->_redis->ping() === '+PONG')
                {
                    $this->_release_lock();
                    if ($this->_redis->close() === FALSE)
                    {
                        return $this->_fail();
                    }
                }
            }
            catch (RedisException $e)
            {
                log_message('error', 'Session: Got RedisException on close(): '.$e->getMessage());
            }

            $this->_redis = NULL;
            return $this->_success;
        }

        return $this->_success;
    }

    public function destroy($session_id)
    {
        if (isset($this->_redis, $this->_lock_key))
        {
            if (($result = $this->_redis->delete($this->_key_prefix.$session_id)) !== 1)
            {
                log_message('debug', 'Session: Redis::delete() expected to return 1, got '.var_export($result, TRUE).' instead.');
            }

            $this->_cookie_destroy();
            return $this->_success;
        }

        return $this->_fail();
    }

    public function gc($maxlifetime)
    {
        return $this->_success;
    }

    protected function _get_lock($session_id)
    {
        if ($this->_lock_key === $this->_key_prefix.$session_id.':lock')
        {
            return $this->_redis->setTimeout($this->_lock_key, 300);
        }

        $lock_key = $this->_key_prefix.$session_id.':lock';
        $attempt = 0;
        do
        {
            if (($ttl = $this->_redis->ttl($lock_key)) > 0)
            {
                sleep(1);
                continue;
            }

            $result = ($ttl === -2)
                ? $this->_redis->set($lock_key, time(), array('nx', 'ex' => 300))
                : $this->_redis->setex($lock_key, 300, time());

            if ( ! $result)
            {
                log_message('error', 'Session: Error while trying to obtain lock for '.$this->_key_prefix.$session_id);
                return FALSE;
            }

            $this->_lock_key = $lock_key;
            break;
        }
        while (++$attempt < 30);

        if ($attempt === 30)
        {
            log_message('error', 'Session: Unable to obtain lock for '.$this->_key_prefix.$session_id.' after 30 attempts, aborting.');
            return FALSE;
        }
        elseif ($ttl === -1)
        {
            log_message('debug', 'Session: Lock for '.$this->_key_prefix.$session_id.' had no TTL, overriding.');
        }

        $this->_lock = TRUE;
        return TRUE;
    }

    protected function _release_lock()
    {
        if (isset($this->_redis, $this->_lock_key) && $this->_lock)
        {
            if ( ! $this->_redis->delete($this->_lock_key))
            {
                log_message('error', 'Session: Error while trying to free lock for '.$this->_lock_key);
                return FALSE;
            }

            $this->_lock_key = NULL;
            $this->_lock = FALSE;
        }

        return TRUE;
    }
}

namespace phpspider\core;

use Exception;
use Redis;

class queue
{
    protected static $redis = NULL;
    protected static $configs = array();
    private static $links = array();
    private static $link_name = 'default';
    public static $prefix = 'phpspider';
    public static $error = '';

    public static function init()
    {
        if (!extension_loaded('redis'))
        {
            self::$error = 'The redis extension was not found';
            return false;
        }

        $config = self::$link_name == 'default' ? self::_get_default_config() : self::$configs[self::$link_name];

        if (empty(self::$links[self::$link_name]))
        {
            self::$links[self::$link_name] = new Redis();
            if (strstr($config['host'], '.sock'))
            {
                if (!self::$links[self::$link_name]->connect($config['host']))
                {
                    self::$error = 'Unable to connect to redis server';
                    unset(self::$links[self::$link_name]);
                    return false;
                }
            }
            else
            {
                if (!self::$links[self::$link_name]->connect($config['host'], $config['port'], $config['timeout']))
                {
                    self::$error = 'Unable to connect to redis server';
                    unset(self::$links[self::$link_name]);
                    return false;
                }
            }

            if ($config['pass'])
            {
                if (!self::$links[self::$link_name]->auth($config['pass']))
                {
                    self::$error = 'Redis Server authentication failed';
                    unset(self::$links[self::$link_name]);
                    return false;
                }
            }

            $prefix = empty($config['prefix']) ? self::$prefix : $config['prefix'];
            self::$links[self::$link_name]->setOption(Redis::OPT_PREFIX, $prefix.':');
            self::$links[self::$link_name]->setOption(Redis::OPT_READ_TIMEOUT, -1);
            self::$links[self::$link_name]->select($config['db']);
        }

        return self::$links[self::$link_name];
    }

    public static function set($key, $value, $expire = 0)
    {
        self::init();
        try
        {
            if (self::$links[self::$link_name])
            {
                if ($expire > 0)
                {
                    return self::$links[self::$link_name]->setex($key, $expire, $value);
                }
                else
                {
                    return self::$links[self::$link_name]->set($key, $value);
                }
            }
        }
        catch (Exception $e)
        {
            $msg = "PHP Fatal error:  Uncaught exception 'RedisException' with message '".$e->getMessage()."'\n";
            log::warn($msg);
            if ($e->getCode() == 0) 
            {
                self::$links[self::$link_name]->close();
                self::$links[self::$link_name] = null;
                usleep(100000);
                return self::set($key, $value, $expire);
            }
        }
        return NULL;
    }

    public static function get($key)
    {
        self::init();
        try
        {
            if (self::$links[self::$link_name])
            {
                return self::$links[self::$link_name]->get($key);
            }
        }
        catch (Exception $e)
        {
            $msg = "PHP Fatal error:  Uncaught exception 'RedisException' with message '".$e->getMessage()."'\n";
            log::warn($msg);
            if ($e->getCode() == 0) 
            {
                self::$links[self::$link_name]->close();
                self::$links[self::$link_name] = null;
                usleep(100000);
                return self::get($key);
            }
        }
        return NULL;
    }

    public static function del($key)
    {
        self::init();
        try
        {
            if (self::$links[self::$link_name])
            {
                return self::$links[self::$link_name]->del($key);
            }
        }
        catch (Exception $e)
        {
            $msg = "PHP Fatal error:  Uncaught exception 'RedisException' with message '".$e->getMessage()."'\n";
            log::warn($msg);
            if ($e->getCode() == 0) 
            {
                self::$links[self::$link_name]->close();
                self::$links[self::$link_name] = null;
                usleep(100000);
                return self::del($key);
            }
        }
        return NULL;
    }

    protected static function _get_default_config()
    {
        if (empty(self::$configs['default']))
        {
            if (!is_array($GLOBALS['config']['redis']))
            {
                exit('cls_redis.php _get_default_config()' . '没有redis配置');
            }
            self::$configs['default'] = $GLOBALS['config']['redis'];
        }
        return self::$configs['default'];
    }

    public static function clear_link()
    {
        if(self::$links) 
        {
            foreach(self::$links as $k=>$v)
            {
                $v->close();
                unset(self::$links[$k]);
            }
        }
    }
}

class CI_Cache_redis extends CI_Driver
{
    protected static $_default_config = array(
        'socket_type' => 'tcp',
        'host' => '127.0.0.1',
        'password' => NULL,
        'port' => 6379,
        'timeout' => 0
    );

    protected $_redis;
    protected $_serialized = array();

    public function __construct()
    {
        if ( ! $this->is_supported())
        {
            log_message('error', 'Cache: Failed to create Redis object; extension not loaded?');
            return;
        }

        $CI =& get_instance();

        if ($CI->config->load('redis', TRUE, TRUE))
        {
            $config = array_merge(self::$_default_config, $CI->config->item('redis'));
        }
        else
        {
            $config = self::$_default_config;
        }

        $this->_redis = new Redis();

        try
        {
            if ($config['socket_type'] === 'unix')
            {
                $success = $this->_redis->connect($config['socket']);
            }
            else
            {
                $success = $this->_redis->connect($config['host'], $config['port'], $config['timeout']);
            }

            if ( ! $success)
            {
                log_message('error', 'Cache: Redis connection failed. Check your configuration.');
            }

            if (isset($config['password']) && ! $this->_redis->auth($config['password']))
            {
                log_message('error', 'Cache: Redis authentication failed.');
            }
        }
        catch (RedisException $e)
        {
            log_message('error', 'Cache: Redis connection refused ('.$e->getMessage().')');
        }

        $serialized = $this->_redis->sMembers('_ci_redis_serialized');
        empty($serialized) OR $this->_serialized = array_flip($serialized);
    }

    public function get($key)
    {
        $value = $this->_redis->get($key);

        if ($value !== FALSE && isset($this->_serialized[$key]))
        {
            return unserialize($value);
        }

        return $value;
    }

    public function save($id, $data, $ttl = 60, $raw = FALSE)
    {
        if (is_array($data) OR is_object($data))
        {
            if ( ! $this->_redis->sIsMember('_ci_redis_serialized', $id) && ! $this->_redis->sAdd('_ci_redis_serialized', $id))
            {
                return FALSE;
            }

            isset($this->_serialized[$id]) OR $this->_serialized[$id] = TRUE;
            $data = serialize($data);
        }
        elseif (isset($this->_serialized[$id]))
        {
            $this->_serialized[$id] = NULL;
            $this->_redis->sRemove('_ci_redis_serialized', $id);
        }

        return $this->_redis->set($id, $data, $ttl);
    }

    public function delete($key)
    {
        if ($this->_redis->delete($key) !== 1)
        {
            return FALSE;
        }

        if (isset($this->_serialized[$key]))
        {
            $this->_serialized[$key] = NULL;
            $this->_redis->sRemove('_ci_redis_serialized', $key);
        }

        return TRUE;
    }

    public function increment($id, $offset = 1)
    {
        return $this->_redis->incr($id, $offset);
    }

    public function decrement($id, $offset = 1)
    {
        return $this->_redis->decr($id, $offset);
    }

    public function clean()
    {
        return $this->_redis->flushDB();
    }

    public function cache_info($type = NULL)
    {
        return $this->_redis->info();
    }

    public function get_metadata($key)
    {
        $value = $this->get($key);

        if ($value !== FALSE)
        {
            return array(
                'expire' => time() + $this->_redis->ttl($key),
                'data' => $value
            );
        }

        return FALSE;
    }

    public function is_supported()
    {
        return extension_loaded('redis');
    }

    public function __destruct()
    {
        if ($this->_redis)
        {
            $this->_redis->close();
        }
    }
}

