function HerokuAPI($method, $url, $data = '', $apikey)
{
    if ($method=='PATCH'||$method=='POST') {
        $headers['Content-Type'] = 'application/json';
    } 
    $headers['Authorization'] = 'Bearer ' . $apikey;
    $headers['Accept'] = 'application/vnd.heroku+json; version=3';
    $sendHeaders = array();
    foreach ($headers as $headerName => $headerVal) {
        $sendHeaders[] = $headerName . ': ' . $headerVal;
    }
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_CUSTOMREQUEST,$method);
    curl_setopt($ch, CURLOPT_POSTFIELDS,$data);
    curl_setopt($ch, CURLOPT_TIMEOUT, 5);
    curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 10);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($ch, CURLOPT_HEADER, 0);
    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);
    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);
    curl_setopt($ch, CURLOPT_HTTPHEADER, $sendHeaders);
    $response['body'] = curl_exec($ch);
    $response['stat'] = curl_getinfo($ch,CURLINFO_HTTP_CODE);
    curl_close($ch);
    return $response;
}

namespace cmf\lib;

abstract class Oauth2
{
    protected $version = '2.0';
    protected $appKey = '';
    protected $appSecret = '';
    protected $responseType = 'code';
    protected $grantType = 'authorization_code';
    protected $callback = '';
    protected $authorize = '';
    protected $getRequestCodeURL = '';
    protected $getAccessTokenURL = '';
    protected $apiBase = '';
    protected $token = null;

    protected function http($url, $params, $method = 'GET', $header = [], $multi = false)
    {
        $opts = [
            CURLOPT_TIMEOUT        => 30,
            CURLOPT_RETURNTRANSFER => 1,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_SSL_VERIFYHOST => false,
            CURLOPT_HTTPHEADER     => $header
        ];

        switch (strtoupper($method)) {
            case 'GET':
                $opts[CURLOPT_URL] = $url . '?' . http_build_query($params);
                break;
            case 'POST':
                $params                   = $multi ? $params : http_build_query($params);
                $opts[CURLOPT_URL]        = $url;
                $opts[CURLOPT_POST]       = 1;
                $opts[CURLOPT_POSTFIELDS] = $params;
                break;
            default:
                throw new \Exception('不支持的请求方式！');
        }

        $ch = curl_init();
        curl_setopt_array($ch, $opts);
        $data  = curl_exec($ch);
        $error = curl_error($ch);
        curl_close($ch);
        if ($error) throw new \Exception('请求发生错误：' . $error);
        return $data;
    }
}

use GuzzleHttp\Client;
use GuzzleHttp\Exception\ConnectException;
use GuzzleHttp\Exception\GuzzleException;
use GuzzleHttp\Exception\ServerException;
use Psr\Http\Message\ResponseInterface;

abstract class SupportedApps
{
    protected $jar = false;
    protected $method = 'GET';
    protected $error;

    public function execute(
        $url,
        array $attrs = [],
        $overridevars = null,
        $overridemethod = null
    ): ?ResponseInterface {
        $res = null;

        $vars = ($overridevars === null || $overridevars === false) ?
         [
            'http_errors' => false,
            'timeout' => 15,
            'connect_timeout' => 15,
        ] : $overridevars;

        $client = new Client($vars);

        $method = ($overridemethod === null || $overridemethod === false) ? $this->method : $overridemethod;

        try {
            return $client->request($method, $url, $attrs);
        } catch (ConnectException $e) {
            $this->error = 'Connection refused - '.(string) $e->getMessage();
        } catch (ServerException $e) {
            $this->error = (string) $e->getResponse()->getBody();
        }
        $this->error = 'General error connecting with API';

        return $res;
    }

    public function appTest($url, array $attrs = []): object
    {
        if (empty($this->config->url)) {
            return (object) [
                'code' => 404,
                'status' => 'No URL has been specified',
                'response' => 'No URL has been specified',
            ];
        }
        $res = $this->execute($url, $attrs);
        if ($res == null) {
            return (object) [
                'code' => null,
                'status' => $this->error,
                'response' => 'Connection failed',
            ];
        }
        switch ($res->getStatusCode()) {
            case 200:
                $status = 'Successfully communicated with the API';
                break;
            case 401:
                $status = 'Failed: Invalid credentials';
                break;
            case 404:
                $status = 'Failed: Please make sure your URL is correct and that there is a trailing slash';
                break;
            default:
                $status = 'Something went wrong... Code: '.$res->getStatusCode();
                break;
        }

        return (object) [
            'code' => $res->getStatusCode(),
            'status' => $status,
            'response' => $res->getBody(),
        ];
    }
}

class AlipayService
{
    protected $appId;
    protected $alipayRootCertSn;
    protected $appCertSn;
    protected $notifyUrl;
    protected $charset;
    protected $rsaPrivateKey;
    protected $totalFee;
    protected $outTradeNo;
    protected $orderName;
    protected $userid;

    public function __construct()
    {
        $this->charset = 'utf-8';
    }

    public function setAppid($appid)
    {
        $this->appId = $appid;
    }

    public function setAlipayRootCertSn($alipayRootCertSn)
    {
        $this->alipayRootCertSn = $alipayRootCertSn;
    }

    public function setAppCertSn($appCertSn)
    {
        $this->appCertSn = $appCertSn;
    }

    public function setNotifyUrl($notifyUrl)
    {
        $this->notifyUrl = $notifyUrl;
    }

    public function setRsaPrivateKey($saPrivateKey)
    {
        $this->rsaPrivateKey = $saPrivateKey;
    }

    public function setTotalFee($payAmount)
    {
        $this->totalFee = $payAmount;
    }

    public function setOutTradeNo($outTradeNo)
    {
        $this->outTradeNo = $outTradeNo;
    }

    public function setOrderName($orderName)
    {
        $this->orderName = $orderName;
    }

    public function setUserId($userid)
    {
        $this->userid = $userid;
    }

    public function sendRedPacket()
    {
        $requestConfigs = array(
            'out_biz_no'=>$this->outTradeNo,
            'trans_amount'=>$this->totalFee,
            'product_code'=>'STD_RED_PACKET',
            'biz_scene'=>'DIRECT_TRANSFER',
            'remark'=>$this->orderName,
            'order_title'=>$this->orderName,
            'payee_info'=>array(
                'identity'=>$this->userid,
                'identity_type'=>'ALIPAY_USER_ID',
            ),
            'business_params'=>array(
                'sub_biz_scene'=>'REDPACKET',
            )
        );
        $commonConfigs = array(
            'alipay_root_cert_sn' => $this->alipayRootCertSn,
            'app_cert_sn' => $this->appCertSn,
            'app_id' => $this->appId,
            'method' => 'alipay.fund.trans.uni.transfer',
            'format' => 'JSON',
            'charset'=>'utf-8',
            'sign_type'=>'RSA2',
            'timestamp'=>date('Y-m-d H:i:s'),
            'version'=>'1.0',
            'biz_content'=>json_encode($requestConfigs),
        );

        $commonConfigs["sign"] = $this->generateSign($commonConfigs, $commonConfigs['sign_type']);
        $result = $this->curlPost('https://openapi.alipay.com/gateway.do?charset='.$this->charset,$commonConfigs);
        return json_decode($result,true);
    }

    public function generateSign($params, $signType = "RSA") {
        return $this->sign($this->getSignContent($params), $signType);
    }

    protected function sign($data, $signType = "RSA") {
        $priKey=$this->rsaPrivateKey;
        $res = "-----BEGIN RSA PRIVATE KEY-----\n" .
            wordwrap($priKey, 64, "\n", true) .
            "\n-----END RSA PRIVATE KEY-----";
        ($res) or die('您使用的私钥格式错误，请检查RSA私钥配置');
        if ("RSA2" == $signType) {
            openssl_sign($data, $sign, $res, version_compare(PHP_VERSION,'5.4.0', '<') ? SHA256 : OPENSSL_ALGO_SHA256);
        } else {
            openssl_sign($data, $sign, $res);
        }
        $sign = base64_encode($sign);
        return $sign;
    }

    protected function checkEmpty($value) {
        if (!isset($value))
            return true;
        if ($value === null)
            return true;
        if (trim($value) === "")
            return true;
        return false;
    }

    public function getSignContent($params) {
        ksort($params);
        $stringToBeSigned = "";
        $i = 0;
        foreach ($params as $k => $v) {
            if (false === $this->checkEmpty($v) && "@" != substr($v, 0, 1)) {
                $v = $this->characet($v, $this->charset);
                if ($i == 0) {
                    $stringToBeSigned .= "$k" . "=" . "$v";
                } else {
                    $stringToBeSigned .= "&" . "$k" . "=" . "$v";
                }
                $i++;
            }
        }
        unset ($k, $v);
        return $stringToBeSigned;
    }

    function characet($data, $targetCharset) {
        if (!empty($data)) {
            $fileType = $this->charset;
            if (strcasecmp($fileType, $targetCharset) != 0) {
                $data = mb_convert_encoding($data, $targetCharset, $fileType);
            }
        }
        return $data;
    }

    public function curlPost($url = '', $postData = '', $options = array())
    {
        if (is_array($postData)) {
            $postData = http_build_query($postData);
        }
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);
        if (!empty($options)) {
            curl_setopt_array($ch, $options);
        }
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        $data = curl_exec($ch);
        curl_close($ch);
        return $data;
    }
}

use Rych\Bencode\Bencode;
use Rych\Bencode\Exception\RuntimeException;

class HttpScraper extends BaseScraper
{
    protected $timeout = 30;

    public function request($url, array $query)
    {
        $ch = curl_init();
        curl_setopt_array($ch, [
            CURLOPT_URL => $url . '?' . join('&', $query),
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_TIMEOUT => $this->timeout,
        ]);

        $result = curl_exec($ch);

        $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        if ($code != 200) {
            $result = null;
        }

        curl_close($ch);

        return $result;
    }
}

use Psy\Exception\ErrorException;
use Psy\Shell;

class CurlDownloader implements Downloader
{
    private $tempDir = null;
    private $outputFile = null;

    public function setTempDir(string $tempDir)
    {
        $this->tempDir = $tempDir;
    }

    public function download(string $url): bool
    {
        $tempDir = $this->tempDir ?: \sys_get_temp_dir();
        $this->outputFile = \tempnam($tempDir, 'psysh-archive-');
        $targetName = $this->outputFile.'.tar.gz';

        if (!\rename($this->outputFile, $targetName)) {
            return false;
        }

        $this->outputFile = $targetName;

        $outputHandle = \fopen($this->outputFile, 'w');
        if (!$outputHandle) {
            return false;
        }
        $curl = \curl_init();
        \curl_setopt_array($curl, [
            \CURLOPT_FAILONERROR    => true,
            \CURLOPT_HEADER         => 0,
            \CURLOPT_FOLLOWLOCATION => true,
            \CURLOPT_TIMEOUT        => 10,
            \CURLOPT_FILE           => $outputHandle,
            \CURLOPT_HTTPHEADER     => [
                'User-Agent' => 'PsySH/'.Shell::VERSION,
            ],
        ]);
        \curl_setopt($curl, \CURLOPT_URL, $url);
        $result = \curl_exec($curl);
        $error = \curl_error($curl);
        \curl_close($curl);

        \fclose($outputHandle);

        if (!$result) {
            throw new ErrorException('cURL Error: '.$error);
        }

        return (bool) $result;
    }

    public function getFilename(): string
    {
        return $this->outputFile;
    }

    public function cleanup()
    {
        if (\file_exists($this->outputFile)) {
            \unlink($this->outputFile);
        }
    }
}

namespace Qiniu\Http;

use Qiniu\Config;

final class Client
{
    public static function get($url, array $headers = array())
    {
        $request = new Request('GET', $url, $headers);
        return self::sendRequest($request);
    }

    public static function delete($url, array $headers = array())
    {
        $request = new Request('DELETE', $url, $headers);
        return self::sendRequest($request);
    }

    public static function post($url, $body, array $headers = array())
    {
        $request = new Request('POST', $url, $headers, $body);
        return self::sendRequest($request);
    }

    public static function PUT($url, $body, array $headers = array())
    {
        $request = new Request('PUT', $url, $headers, $body);
        return self::sendRequest($request);
    }

    public static function multipartPost(
        $url,
        $fields,
        $name,
        $fileName,
        $fileBody,
        $mimeType = null,
        array $headers = array()
    ) {
        $data = array();
        $mimeBoundary = md5(microtime());

        foreach ($fields as $key => $val) {
            array_push($data, '--' . $mimeBoundary);
            array_push($data, "Content-Disposition: form-data; name=\"$key\"");
            array_push($data, '');
            array_push($data, $val);
        }

        array_push($data, '--' . $mimeBoundary);
        $finalMimeType = empty($mimeType) ? 'application/octet-stream' : $mimeType;
        $finalFileName = self::escapeQuotes($fileName);
        array_push($data, "Content-Disposition: form-data; name=\"$name\"; filename=\"$finalFileName\"");
        array_push($data, "Content-Type: $finalMimeType");
        array_push($data, '');
        array_push($data, $fileBody);

        array_push($data, '--' . $mimeBoundary . '--');
        array_push($data, '');

        $body = implode("\r\n", $data);
        $contentType = 'multipart/form-data; boundary=' . $mimeBoundary;
        $headers['Content-Type'] = $contentType;
        $request = new Request('POST', $url, $headers, $body);
        return self::sendRequest($request);
    }

    public static function sendRequest($request)
    {
        $t1 = microtime(true);
        $ch = curl_init();
        $options = array(
            CURLOPT_USERAGENT => self::userAgent(),
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_SSL_VERIFYHOST => false,
            CURLOPT_HEADER => true,
            CURLOPT_NOBODY => false,
            CURLOPT_CUSTOMREQUEST => $request->method,
            CURLOPT_URL => $request->url,
        );
        if (!ini_get('safe_mode') && !ini_get('open_basedir')) {
            $options[CURLOPT_FOLLOWLOCATION] = true;
        }
        if (!empty($request->headers)) {
            $headers = array();
            foreach ($request->headers as $key => $val) {
                array_push($headers, "$key: $val");
            }
            $options[CURLOPT_HTTPHEADER] = $headers;
        }
        curl_setopt($ch, CURLOPT_HTTPHEADER, array('Expect:'));
        if (!empty($request->body)) {
            $options[CURLOPT_POSTFIELDS] = $request->body;
        }
        curl_setopt_array($ch, $options);
        $result = curl_exec($ch);
        $t2 = microtime(true);
        $duration = round($t2 - $t1, 3);
        $ret = curl_errno($ch);
        if ($ret !== 0) {
            $r = new Response(-1, $duration, array(), null, curl_error($ch));
            curl_close($ch);
            return $r;
        }
        $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $header_size = curl_getinfo($ch, CURLINFO_HEADER_SIZE);
        $headers = self::parseHeaders(substr($result, 0, $header_size));
        $body = substr($result, $header_size);
        curl_close($ch);
        return new Response($code, $duration, $headers, $body, null);
    }

    private static function userAgent()
    {
        $sdkInfo = "QiniuPHP/" . Config::SDK_VER;
        $systemInfo = php_uname("s");
        $machineInfo = php_uname("m");
        $envInfo = "($systemInfo/$machineInfo)";
        $phpVer = phpversion();
        $ua = "$sdkInfo $envInfo PHP/$phpVer";
        return $ua;
    }

    private static function parseHeaders($raw)
    {
        $headers = array();
        $headerLines = explode("\r\n", $raw);
        foreach ($headerLines as $line) {
            $headerLine = trim($line);
            $kv = explode(':', $headerLine);
            if (count($kv) > 1) {
                $kv[0] =self::ucwordsHyphen($kv[0]);
                $headers[$kv[0]] = trim($kv[1]);
            }
        }
        return $headers;
    }

    private static function escapeQuotes($str)
    {
        $find = array("\\", "\"");
        $replace = array("\\\\", "\\\"");
        return str_replace($find, $replace, $str);
    }

    private static function ucwordsHyphen($str)
    {
        return str_replace('- ', '-', ucwords(str_replace('-', '- ', $str)));
    }
}

function dogcloud_upload($object, $file, $mime)
{
    if (!@file_exists($file)) {
        return false;
    }
    if (@file_exists($file)) {
        $accessKey = kratos_option('g_cos_fieldset')['g_cos_accesskey'];
        $secretKey = kratos_option('g_cos_fieldset')['g_cos_secretkey'];
        $bucket = kratos_option('g_cos_fieldset')['g_cos_bucketname'];

        $filesize = fileSize($file);
        $file = fopen($file, 'rb');

        $signStr = "/oss/upload/put.json?bucket=$bucket&key=$object" . "\n" . "";
        $sign = hash_hmac('sha1', $signStr, $secretKey);
        $authorization = "TOKEN " . $accessKey . ":" . $sign;

        $url = "https://api.dogecloud.com/oss/upload/put.json?bucket=$bucket&key=$object";
        $headers = array("Host: api.dogecloud.com", "Content-Type: $mime", "Authorization: $authorization");

        $curl = curl_init();
        curl_setopt_array($curl, array(
            CURLOPT_URL => $url,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_PUT => true,
            CURLOPT_TIMEOUT => 0,
            CURLOPT_FOLLOWLOCATION => true,
            CURLOPT_HTTP_VERSION => CURL_HTTP_VERSION_1_1,
            CURLOPT_CUSTOMREQUEST => "PUT",
            CURLOPT_INFILE => $file,
            CURLOPT_INFILESIZE => $filesize,
            CURLOPT_HTTPHEADER => $headers,
        ));
        $response = curl_exec($curl);
        curl_close($curl);
    } else {
        return false;
    }
}

class Downloader {
    static function start($url,$saveFile,$timeout = 10) {
        $dataFile = $saveFile . '.download.cfg';
        $saveTemp = $saveFile . '.downloading';

        if(is_array($url)){
            $fileHeader = $url;
        }else{
            $fileHeader = url_header($url);
        }
        $url = $fileHeader['url'];
        if(!$url){
            return array('code'=>false,'data'=>'url error!');
        }
        if(!$fileHeader['supportRange'] || $fileHeader['length'] == 0 ){
            @unlink($saveTemp);@unlink($saveFile);
            $result = self::fileDownloadFopen($url,$saveFile,$fileHeader['length']);
            if($result['code']) {
                return $result;
            }else{
                @unlink($saveTemp);@unlink($saveFile);
                $result = self::fileDownloadCurl($url,$saveFile,false,0,$fileHeader['length']);
                @unlink($saveTemp);@unlink($saveFile);
                return $result;
            }
        }

        $existsLength  = is_file($saveTemp) ? filesize($saveTemp) : 0;
        $contentLength = intval($fileHeader['length']);
        if( file_exists($saveTemp) && time() - filemtime($saveTemp) < 3) {
            return array('code'=>false,'data'=>'downloading');
        }

        $existsData = array();
        if(is_file($dataFile)){
            $tempData = file_get_contents($dataFile);
            $existsData = json_decode($tempData, 1);
        }
        if( file_exists($saveFile) && $contentLength == filesize($saveFile)){
            @unlink($saveTemp);
            @unlink($dataFile);
            return array('code'=>true,'data'=>'exist');
        }

        if ($existsData['length'] != $contentLength) {
            $existsData = array('length' => $contentLength);
        }
        if($existsLength > $contentLength){
            @unlink($saveTemp);
        }
        file_put_contents($dataFile, json_encode($existsData));
        $result = self::fileDownloadCurl($url,$saveFile,true,$existsLength,$contentLength);
        if($result['code']){
            @unlink($dataFile);
        }
        return $result;
    }

    static function fileDownloadFopen($url, $fileName,$headerSize=0){
        @ini_set('user_agent','Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/27.0.1453.94 Safari/537.36');

        $fileTemp = $fileName.'.downloading';
        @set_time_limit(0);
        @unlink($fileTemp);
        if ($fp = @fopen ($url, "rb")){
            if(!$downloadFp = @fopen($fileTemp, "wb")){
                return array('code'=>false,'data'=>'open_downloading_error');
            }
            while(!feof($fp)){
                if(!file_exists($fileTemp)){
                    fclose($downloadFp);
                    return array('code'=>false,'data'=>'stoped');
                }
                clearstatcache();
                if( $headerSize>0 && $headerSize==get_filesize(iconv_system($fileTemp))){
                    break;
                }
                fwrite($downloadFp, fread($fp, 1024 * 8 ), 1024 * 8);
            }
            fclose($downloadFp);
            fclose($fp);
            self::checkGzip($fileTemp);
            if(!@rename($fileTemp,$fileName)){
                usleep(round(rand(0,1000)*50));
                @unlink($fileName);
                $res = @rename($fileTemp,$fileName);
                if(!$res){
                    return array('code'=>false,'data'=>'rename error![open]');
                }
            }
            return array('code'=>true,'data'=>'success');
        }else{
            return array('code'=>false,'data'=>'url_open_error');
        }
    }

    static function fileDownloadCurl($url, $fileName,$supportRange=false,$existsLength=0,$length=0){
        $fileTemp = $fileName.'.downloading';
        @set_time_limit(0);
        if ($fp = @fopen ($fileTemp, "a")){
            $ch = curl_init($url);
            if($supportRange){
                curl_setopt($ch, CURLOPT_RANGE, $existsLength."-");
            }
            curl_setopt($ch, CURLOPT_FILE, $fp);
            curl_setopt($ch, CURLOPT_REFERER,get_url_link($url));
            curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);
            curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 0);
            curl_setopt($ch, CURLOPT_USERAGENT,'Mozilla/5.0 (Windows NT 6.2; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/27.0.1453.94 Safari/537.36');

            $res = curl_exec($ch);
            curl_close($ch);
            fclose($fp);

            $filesize = get_filesize(iconv_system($fileTemp));
            if($filesize < $length && $length!=0){
                return array('code'=>false,'data'=>'downloading');
            }
            if($res && filesize($fileTemp) != 0){
                self::checkGzip($fileTemp);
                if(!@rename($fileTemp,$fileName)){
                    @unlink($fileName);
                    $res = @rename($fileTemp,$fileName);
                    if(!$res){
                        return array('code'=>false,'data'=>'rename error![curl]');
                    }
                }
                return array('code'=>true,'data'=>'success');
            }
            return array('code'=>false,'data'=>'curl exec error!');
        }else{
            return array('code'=>false,'data'=>'file create error');
        }
    }

    static function checkGzip($file){
        $char = "\x1f\x8b";
        $str  = file_sub_str($file,0,2);
        if($char != $str) return;

        ob_start();   
        readgzfile($file);   
        $out = ob_get_clean();
        file_put_contents($file,$out);
    }
}

use Think\Upload\Driver\Bcs\BCS_RequestCore_Exception as BCS_RequestCore_Exception;

class BcsRequestcore
{
    public $request_url;
    public $request_headers;
    public $request_body;
    public $response;
    public $response_headers;
    public $response_body;
    public $response_code;
    public $response_info;
    public $curl_handle;
    public $method;
    public $proxy = null;
    public $username = null;
    public $password = null;
    public $curlopts = null;
    public $debug_mode = false;
    public $request_class = 'BCS_RequestCore';
    public $response_class = 'BCS_ResponseCore';
    public $useragent = 'BCS_RequestCore/1.4.2';
    public $read_file = null;
    public $read_stream = null;
    public $read_stream_size = null;
    public $read_stream_read = 0;
    public $write_file = null;
    public $write_stream = null;
    public $seek_position = null;
    public $registered_streaming_read_callback = null;
    public $registered_streaming_write_callback = null;

    const HTTP_GET = 'GET';
    const HTTP_POST = 'POST';
    const HTTP_PUT = 'PUT';
    const HTTP_DELETE = 'DELETE';
    const HTTP_HEAD = 'HEAD';

    public function __construct($url = null, $proxy = null, $helpers = null)
    {
        $this->request_url = $url;
        $this->method = self::HTTP_GET;
        $this->request_headers = array();
        $this->request_body = '';

        if (isset($helpers['request']) && !empty($helpers['request'])) {
            $this->request_class = $helpers['request'];
        }

        if (isset($helpers['response']) && !empty($helpers['response'])) {
            $this->response_class = $helpers['response'];
        }

        if ($proxy) {
            $this->setProxy($proxy);
        }

        return $this;
    }

    public function __destruct()
    {
        if (isset($this->read_file) && isset($this->read_stream)) {
            fclose($this->read_stream);
        }
        if (isset($this->write_file) && isset($this->write_stream)) {
            fclose($this->write_stream);
        }
        return $this;
    }

    public function setCredentials($user, $pass)
    {
        $this->username = $user;
        $this->password = $pass;
        return $this;
    }

    public function addHeader($key, $value)
    {
        $this->request_headers[$key] = $value;
        return $this;
    }

    public function removeHeader($key)
    {
        if (isset($this->request_headers[$key])) {
            unset($this->request_headers[$key]);
        }
        return $this;
    }

    public function setMethod($method)
    {
        $this->method = strtoupper($method);
        return $this;
    }

    public function setUseragent($ua)
    {
        $this->useragent = $ua;
        return $this;
    }

    public function setBody($body)
    {
        $this->request_body = $body;
        return $this;
    }

    public function setRequestUrl($url)
    {
        $this->request_url = $url;
        return $this;
    }

    public function setCurlopts($curlopts)
    {
        $this->curlopts = $curlopts;
        return $this;
    }

    public function setReadStreamSize($size)
    {
        $this->read_stream_size = $size;
        return $this;
    }

    public function setReadStream($resource, $size = null)
    {
        if (!isset($size) || $size < 0) {
            $stats = fstat($resource);
            if ($stats && $stats['size'] >= 0) {
                $position = ftell($resource);
                if (false !== $position && $position >= 0) {
                    $size = $stats['size'] - $position;
                }
            }
        }
        $this->read_stream = $resource;
        return $this->setReadStreamSize($size);
    }

    public function setReadFile($location)
    {
        $this->read_file = $location;
        $read_file_handle = fopen($location, 'r');
        return $this->setReadStream($read_file_handle);
    }

    public function setWriteStream($resource)
    {
        $this->write_stream = $resource;
        return $this;
    }

    public function setWriteFile($location)
    {
        $this->write_file = $location;
        $write_file_handle = fopen($location, 'w');
        return $this->setWriteStream($write_file_handle);
    }

    public function setProxy($proxy)
    {
        $proxy = parse_url($proxy);
        $proxy['user'] = isset($proxy['user']) ? $proxy['user'] : null;
        $proxy['pass'] = isset($proxy['pass']) ? $proxy['pass'] : null;
        $proxy['port'] = isset($proxy['port']) ? $proxy['port'] : null;
        $this->proxy = $proxy;
        return $this;
    }

    public function setSeekPosition($position)
    {
        $this->seek_position = isset($position) ? (integer) $position : null;
        return $this;
    }

    public function registerStreamingReadCallback($callback)
    {
        $this->registered_streaming_read_callback = $callback;
        return $this;
    }

    public function registerStreamingWriteCallback($callback)
    {
        $this->registered_streaming_write_callback = $callback;
        return $this;
    }

    public function prepRequest()
    {
        $curl_handle = curl_init();
        curl_setopt($curl_handle, CURLOPT_URL, $this->request_url);
        curl_setopt($curl_handle, CURLOPT_FILETIME, true);
        curl_setopt($curl_handle, CURLOPT_FRESH_CONNECT, false);
        curl_setopt($curl_handle, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($curl_handle, CURLOPT_SSL_VERIFYHOST, true);
        curl_setopt($curl_handle, CURLOPT_CLOSEPOLICY, CURLCLOSEPOLICY_LEAST_RECENTLY_USED);
        curl_setopt($curl_handle, CURLOPT_MAXREDIRS, 5);
        curl_setopt($curl_handle, CURLOPT_HEADER, true);
        curl_setopt($curl_handle, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl_handle, CURLOPT_TIMEOUT, 5184000);
        curl_setopt($curl_handle, CURLOPT_CONNECTTIMEOUT, 120);
        curl_setopt($curl_handle, CURLOPT_NOSIGNAL, true);
        curl_setopt($curl_handle, CURLOPT_REFERER, $this->request_url);
        curl_setopt($curl_handle, CURLOPT_USERAGENT, $this->useragent);
        curl_setopt($curl_handle, CURLOPT_READFUNCTION, array($this, 'streaming_read_callback'));

        if ($this->debug_mode) {
            curl_setopt($curl_handle, CURLOPT_VERBOSE, true);
        }

        if ($this->proxy) {
            curl_setopt($curl_handle, CURLOPT_HTTPPROXYTUNNEL, true);
            $host = $this->proxy['host'];
            $host .= ($this->proxy['port']) ? ':' . $this->proxy['port'] : '';
            curl_setopt($curl_handle, CURLOPT_PROXY, $host);
            if (isset($this->proxy['user']) && isset($this->proxy['pass'])) {
                curl_setopt($curl_handle, CURLOPT_PROXYUSERPWD, $this->proxy['user'] . ':' . $this->proxy['pass']);
            }
        }

        if ($this->username && $this->password) {
            curl_setopt($curl_handle, CURLOPT_HTTPAUTH, CURLAUTH_ANY);
            curl_setopt($curl_handle, CURLOPT_USERPWD, $this->username . ':' . $this->password);
        }

        if (extension_loaded('zlib')) {
            curl_setopt($curl_handle, CURLOPT_ENCODING, '');
        }

        if (isset($this->request_headers) && count($this->request_headers)) {
            $temp_headers = array();
            foreach ($this->request_headers as $k => $v) {
                $temp_headers[] = $k . ': ' . $v;
            }
            curl_setopt($curl_handle, CURLOPT_HTTPHEADER, $temp_headers);
        }

        switch ($this->method) {
            case self::HTTP_PUT:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, 'PUT');
                if (isset($this->read_stream)) {
                    if (!isset($this->read_stream_size) || $this->read_stream_size < 0) {
                        throw new BCS_RequestCore_Exception('The stream size for the streaming upload cannot be determined.');
                    }
                    curl_setopt($curl_handle, CURLOPT_INFILESIZE, $this->read_stream_size);
                    curl_setopt($curl_handle, CURLOPT_UPLOAD, true);
                } else {
                    curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                }
                break;
            case self::HTTP_POST:
                curl_setopt($curl_handle, CURLOPT_POST, true);
                curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                break;
            case self::HTTP_HEAD:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, self::HTTP_HEAD);
                curl_setopt($curl_handle, CURLOPT_NOBODY, 1);
                break;
            default:
                curl_setopt($curl_handle, CURLOPT_CUSTOMREQUEST, $this->method);
                if (isset($this->write_stream)) {
                    curl_setopt($curl_handle, CURLOPT_WRITEFUNCTION, array($this, 'streaming_write_callback'));
                    curl_setopt($curl_handle, CURLOPT_HEADER, false);
                } else {
                    curl_setopt($curl_handle, CURLOPT_POSTFIELDS, $this->request_body);
                }
                break;
        }

        if (isset($this->curlopts) && sizeof($this->curlopts) > 0) {
            foreach ($this->curlopts as $k => $v) {
                curl_setopt($curl_handle, $k, $v);
            }
        }

        return $curl_handle;
    }

    public function sendRequest($parse = false)
    {
        if (false === $this->isBaeEnv()) {
            set_time_limit(0);
        }

        $curl_handle = $this->prepRequest();
        $this->response = curl_exec($curl_handle);

        if (false === $this->response ||
            (self::HTTP_GET === $this->method &&
                curl_errno($curl_handle) === CURLE_PARTIAL_FILE)) {
            throw new BCS_RequestCore_Exception('cURL resource: ' . (string) $curl_handle . '; cURL error: ' . curl_error($curl_handle) . ' (' . curl_errno($curl_handle) . ')');
        }

        $parsed_response = $this->processResponse($curl_handle, $this->response);
        curl_close($curl_handle);

        if ($parse) {
            return $parsed_response;
        }

        return $this->response;
    }

    public function processResponse($curl_handle = null, $response = null)
    {
        if ($curl_handle && $response) {
            $this->curl_handle = $curl_handle;
            $this->response = $response;
        }

        if (is_resource($this->curl_handle)) {
            $header_size = curl_getinfo($this->curl_handle, CURLINFO_HEADER_SIZE);
            $this->response_headers = substr($this->response, 0, $header_size);
            $this->response_body = substr($this->response, $header_size);
            $this->response_code = curl_getinfo($this->curl_handle, CURLINFO_HTTP_CODE);
            $this->response_info = curl_getinfo($this->curl_handle);

            $this->response_headers = explode("\r\n\r\n", trim($this->response_headers));
            $this->response_headers = array_pop($this->response_headers);
            $this->response_headers = explode("\r\n", $this->response_headers);
            array_shift($this->response_headers);

            $header_assoc = array();
            foreach ($this->response_headers as $header) {
                $kv = explode(': ', $header);
                $header_assoc[$kv[0]] = $kv[1];
            }

            $this->response_headers = $header_assoc;
            $this->response_headers['_info'] = $this->response_info;
            $this->response_headers['_info']['method'] = $this->method;

            if ($curl_handle && $response) {
                $class = '\Think\Upload\Driver\Bcs\\' . $this->response_class;
                return new $class($this->response_headers, $this->response_body, $this->response_code, $this->curl_handle);
            }
        }

        return false;
    }

    private function isBaeEnv()
    {
        if (isset($_SERVER['HTTP_HOST'])) {
            $host = $_SERVER['HTTP_HOST'];
            $pos = strpos($host, '.');
            if (false !== $pos) {
                $substr = substr($host, $pos + 1);
                if ('duapp.com' == $substr) {
                    return true;
                }
            }
        }
        if (isset($_SERVER["HTTP_BAE_LOGID"])) {
            return true;
        }

        return false;
    }

    public function getResponseHeader($header = null)
    {
        if ($header) {
            return $this->response_headers[$header];
        }
        return $this->response_headers;
    }

    public function getResponseBody()
    {
        return $this->response_body;
    }

    public function getResponseCode()
    {
        return $this->response_code;
    }
}

namespace Hhxsv5\LaravelS\Components\HttpClient;

use Swoole\Coroutine\Http\Client as CoroutineClient;

class SimpleHttpClient
{
    protected $curlOptions = [
        CURLOPT_HEADER         => true,
        CURLOPT_FOLLOWLOCATION => true,
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_MAXREDIRS      => 3,
        CURLOPT_TIMEOUT        => 5,
        CURLOPT_CONNECTTIMEOUT => 3,
    ];

    public function httpGet($url, array $options)
    {
        $handle = curl_init();
        $finalOptions = [
                CURLOPT_URL     => $url,
                CURLOPT_HTTPGET => true,
            ] + $this->curlOptions;
        if (isset($options['timeout'])) {
            $finalOptions[CURLOPT_TIMEOUT] = $options['timeout'];
        }
        curl_setopt_array($handle, $finalOptions);
        $responseStr = curl_exec($handle);
        $errno = curl_errno($handle);
        $errmsg = curl_error($handle);
        if ($errno !== 0 || $errmsg !== '') {
            curl_close($handle);
            $msg = sprintf('Failed to send Http request(%s), errcode=%d, errmsg=%s', $url, $errno, $errmsg);
            throw new \RuntimeException($msg, $errno);
        }

        $headerSize = curl_getinfo($handle, CURLINFO_HEADER_SIZE);
        $statusCode = curl_getinfo($handle, CURLINFO_HTTP_CODE);
        curl_close($handle);

        $header = substr($responseStr, 0, $headerSize);
        $body = substr($responseStr, $headerSize);
        $lines = explode("\n", $header);
        array_shift($lines);

        $headers = [];
        foreach ($lines as $part) {
            $middle = explode(':', $part);
            $key = trim($middle[0]);
            if ($key === '') {
                continue;
            }
            if (isset($headers[$key])) {
                $headers[$key] = (array)$headers[$key];
                $headers[$key][] = isset($middle[1]) ? trim($middle[1]) : '';
            } else {
                $headers[$key] = isset($middle[1]) ? trim($middle[1]) : '';
            }
        }
        return ['statusCode' => $statusCode, 'headers' => $headers, 'body' => $body];
    }
}

use Think\Upload\Driver\Bcs\BaiduBCS;

class Bcs
{
    private $rootPath;
    const DEFAULT_URL = 'bcs.duapp.com';
    private $error = '';
    public $config = array(
        'AccessKey'=> '',
        'SecretKey'=> '',
        'bucket'   => '',
        'rename'   => false,
        'timeout'  => 3600,
    );
    public $bcs = null;

    public function __construct($config)
    {
        $this->config = array_merge($this->config, $config);
        $bcsClass = dirname(__FILE__). "/Bcs/bcs.class.php";
        if(is_file($bcsClass))
            require_once($bcsClass);
        $this->bcs = new BaiduBCS($this->config['AccessKey'], $this->config['SecretKey'], self::DEFAULT_URL);
    }

    private function request($path, $method, $headers = null, $body = null)
    {
        $ch = curl_init($path);
        $_headers = array('Expect:');
        if (!is_null($headers) && is_array($headers)){
            foreach($headers as $k => $v) {
                array_push($_headers, "{$k}: {$v}");
            }
        }

        $length = 0;
        $date = gmdate('D, d M Y H:i:s \G\M\T');
        if (!is_null($body)) {
            if(is_resource($body)){
                fseek($body, 0, SEEK_END);
                $length = ftell($body);
                fseek($body, 0);
                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_INFILE, $body);
                curl_setopt($ch, CURLOPT_INFILESIZE, $length);
            } else {
                $length = @strlen($body);
                array_push($_headers, "Content-Length: {$length}");
                curl_setopt($ch, CURLOPT_POSTFIELDS, $body);
            }
        } else {
            array_push($_headers, "Content-Length: {$length}");
        }

        array_push($_headers, "Date: {$date}");
        curl_setopt($ch, CURLOPT_HTTPHEADER, $_headers);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->config['timeout']);
        curl_setopt($ch, CURLOPT_HEADER, 1);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, 0);
        curl_setopt($ch, CURLOPT_CUSTOMREQUEST, $method);

        if ($method == 'PUT' || $method == 'POST') {
            curl_setopt($ch, CURLOPT_POST, 1);
        } else {
            curl_setopt($ch, CURLOPT_POST, 0);
        }

        if ($method == 'HEAD') {
            curl_setopt($ch, CURLOPT_NOBODY, true);
        }

        $response = curl_exec($ch);
        $status = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        list($header, $body) = explode("\r\n\r\n", $response, 2);

        if ($status == 200) {
            if ($method == 'GET') {
                return $body;
            } else {
                $data = $this->response($header);
                return count($data) > 0 ? $data : true;
            }
        } else {
            $this->error($header);
            return false;
        }
    }

    private function response($text)
    {
        $items = json_decode($text, true);
        return $items;
    }

    private function error($header)
    {
        list($status, $stash) = explode("\r\n", $header, 2);
        list($v, $code, $message) = explode(" ", $status, 3);
        $message = is_null($message) ? 'File Not Found' : "[{$status}]:{$message}";
        $this->error = $message;
    }
}

use GuzzleHttp\Client;
use Upyun\Config;
use Upyun\Signature;

class SyncVideo {
    protected $config;

    public function __construct(Config $config)
    {
        $this->config = $config;
    }

    public function process($params, $path) {
        $client = new Client([
            'timeout' => $this->config->timeout,
        ]);

        $path = '/' . $this->config->serviceName . $path;
        $method = 'POST';
        $signedHeaders = Signature::getHeaderSign($this->config, $method, $path);

        $url = $this->config->getSyncVideoEndPoint() . $path;
        $response = $client->request($method, $url, [
            'headers' => $signedHeaders,
            'json' => $params
        ]);

        $body = $response->getBody()->getContents();
        return json_decode($body, true);
    }
}

class AlipayService
{
    protected $appId;
    protected $charset;
    protected $notifyUrl;
    protected $rsaPrivateKey;
    protected $totalFee;
    protected $outTradeNo;
    protected $orderName;
    protected $tradeNo;
    protected $tranOut;
    protected $tranIn;
    protected $amountPercentage;

    public function __construct()
    {
        $this->charset = 'utf-8';
    }

    public function setAppid($appid)
    {
        $this->appId = $appid;
    }

    public function setTradeNo($tradeNo)
    {
        $this->tradeNo = $tradeNo;
    }

    public function setTranOut($tranOut)
    {
        $this->tranOut = $tranOut;
    }

    public function setTranIn($tranIn)
    {
        $this->tranIn = $tranIn;
    }

    public function setRsaPrivateKey($rsaPrivateKey)
    {
        $this->rsaPrivateKey = $rsaPrivateKey;
    }

    public function setTotalFee($payAmount)
    {
        $this->totalFee = $payAmount;
    }

    public function setOutTradeNo($outTradeNo)
    {
        $this->outTradeNo = $outTradeNo;
    }

    public function doSettle()
    {
        $requestConfigs = array(
            'out_request_no'=>$this->outTradeNo,
            'trade_no'=>$this->tradeNo,
            'royalty_parameters'=>array(
                array(
                    'trans_out'=>$this->tranOut,
                    'trans_in'=>$this->tranIn,
                    'amount'=>$this->totalFee,
                    'desc'=>'分账给'.$this->tranIn,
                )
            ),
            'operator_id'=>'A001',
        );
        $commonConfigs = array(
            'app_id' => $this->appId,
            'method' => 'alipay.trade.order.settle',
            'format' => 'JSON',
            'charset'=>$this->charset,
            'sign_type'=>'RSA2',
            'timestamp'=>date('Y-m-d H:i:s'),
            'version'=>'1.0',
            'app_auth_token'=>'',
            'biz_content'=>json_encode($requestConfigs),
        );
        $commonConfigs["sign"] = $this->generateSign($commonConfigs, $commonConfigs['sign_type']);
        $result = $this->curlPost('https://openapi.alipay.com/gateway.do?charset='.$this->charset,$commonConfigs);
        return json_decode($result,true);
    }

    public function generateSign($params, $signType = "RSA") {
        return $this->sign($this->getSignContent($params), $signType);
    }

    protected function sign($data, $signType = "RSA") {
        $priKey=$this->rsaPrivateKey;
        $res = "-----BEGIN RSA PRIVATE KEY-----\n" .
            wordwrap($priKey, 64, "\n", true) .
            "\n-----END RSA PRIVATE KEY-----";
        ($res) or die('您使用的私钥格式错误，请检查RSA私钥配置');
        if ("RSA2" == $signType) {
            openssl_sign($data, $sign, $res, version_compare(PHP_VERSION,'5.4.0', '<') ? SHA256 : OPENSSL_ALGO_SHA256);
        } else {
            openssl_sign($data, $sign, $res);
        }
        $sign = base64_encode($sign);
        return $sign;
    }

    protected function checkEmpty($value) {
        if (!isset($value))
            return true;
        if ($value === null)
            return true;
        if (trim($value) === "")
            return true;
        return false;
    }

    public function getSignContent($params) {
        ksort($params);
        $stringToBeSigned = "";
        $i = 0;
        foreach ($params as $k => $v) {
            if (false === $this->checkEmpty($v) && "@" != substr($v, 0, 1)) {
                $v = $this->characet($v, $this->charset);
                if ($i == 0) {
                    $stringToBeSigned .= "$k" . "=" . "$v";
                } else {
                    $stringToBeSigned .= "&" . "$k" . "=" . "$v";
                }
                $i++;
            }
        }
        unset ($k, $v);
        return $stringToBeSigned;
    }

    function characet($data, $targetCharset) {
        if (!empty($data)) {
            $fileType = $this->charset;
            if (strcasecmp($fileType, $targetCharset) != 0) {
                $data = mb_convert_encoding($data, $targetCharset, $fileType);
            }
        }
        return $data;
    }

    public function curlPost($url = '', $postData = '', $options = array())
    {
        if (is_array($postData)) {
            $postData = http_build_query($postData);
        }
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $postData);
        curl_setopt($ch, CURLOPT_TIMEOUT, 30);
        if (!empty($options)) {
            curl_setopt_array($ch, $options);
        }
        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
        curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);
        $data = curl_exec($ch);
        curl_close($ch);
        return $data;
    }
}

require_once('HproseCommon.php');
require_once('HproseIO.php');
require_once('HproseClient.php');

class HproseHttpClient extends HproseBaseHttpClient {
    private $curl;
    protected function formatCookie($cookies) {
        if (count($cookies) > 0) {
            return "Cookie: " . implode('; ', $cookies);
        }
        return '';
    }
    public function __construct($url = '') {
        parent::__construct($url);
        $this->curl = curl_init();
    }
    protected function send($request) {
        curl_setopt($this->curl, CURLOPT_URL, $this->url);
        curl_setopt($this->curl, CURLOPT_HEADER, TRUE);
        curl_setopt($this->curl, CURLOPT_SSL_VERIFYPEER, FALSE);
        curl_setopt($this->curl, CURLOPT_RETURNTRANSFER, TRUE);
        curl_setopt($this->curl, CURLOPT_POST, TRUE);
        curl_setopt($this->curl, CURLOPT_POSTFIELDS, $request);
        $headers_array = array($this->getCookie(),
                                "Content-Length: " . strlen($request));
        if ($this->keepAlive) {
            $headers_array[] = "Connection: keep-alive";
            $headers_array[] = "Keep-Alive: " . $this->keepAliveTimeout;
        }
        else {
            $headers_array[] = "Connection: close";
        }
        foreach ($this->header as $name => $value) {
            $headers_array[] = $name . ": " . $value;
        }
        curl_setopt($this->curl, CURLOPT_HTTPHEADER, $headers_array);
        if ($this->proxy) {
            curl_setopt($this->curl, CURLOPT_PROXY, $this->proxy);
        }
        if (defined(CURLOPT_TIMEOUT_MS)) {
            curl_setopt($this->curl, CURLOPT_TIMEOUT_MS, $this->timeout);
        }
        else {
            curl_setopt($this->curl, CURLOPT_TIMEOUT, $this->timeout / 1000);
        }
        $response = curl_exec($this->curl);
        $errno = curl_errno($this->curl);
        if ($errno) {
            throw new HproseException($errno . ": " . curl_error($this->curl));
        }
        do {
            list($response_headers, $response) = explode("\r\n\r\n", $response, 2); 
            $http_response_header = explode("\r\n", $response_headers);
            $http_response_firstline = array_shift($http_response_header); 
            if (preg_match('@^HTTP/[0-9]\.[0-9]\s([0-9]{3})\s(.*)@',
                           $http_response_firstline, $matches)) { 
                $response_code = $matches[1];
                $response_status = trim($matches[2]);
            }
            else {
                $response_code = "500";
                $response_status = "Unknown Error.";                
            }
        } while (substr($response_code, 0, 1) == "1");
        if ($response_code != '200') {
            throw new HproseException($response_code . ": " . $response_status);
        }
        $this->setCookie($http_response_header);
        return $response;
    }
    public function __destruct() {
        curl_close($this->curl);
    }
}

