class Qiniu_HttpClient
{
    public function RoundTrip($req) // => ($resp, $error)
    {
        $ch = curl_init();
        $url = $req->URL;
        $options = array(
            CURLOPT_USERAGENT => $req->UA,
            CURLOPT_RETURNTRANSFER => true,
            CURLOPT_SSL_VERIFYPEER => false,
            CURLOPT_SSL_VERIFYHOST => false,
            CURLOPT_HEADER => true,
            CURLOPT_NOBODY => false,
            CURLOPT_CUSTOMREQUEST  => 'POST',
            CURLOPT_URL => $url['path']
        );
        $httpHeader = $req->Header;
        if (!empty($httpHeader))
        {
            $header = array();
            foreach($httpHeader as $key => $parsedUrlValue) {
                $header[] = "$key: $parsedUrlValue";
            }
            $options[CURLOPT_HTTPHEADER] = $header;
        }
        $body = $req->Body;
        if (!empty($body)) {
            $options[CURLOPT_POSTFIELDS] = $body;
        } else {
            $options[CURLOPT_POSTFIELDS] = "";
        }
        curl_setopt_array($ch, $options);
        $result = curl_exec($ch);
        $ret = curl_errno($ch);
        if ($ret !== 0) {
            $err = new Qiniu_Error(0, curl_error($ch));
            curl_close($ch);
            return array(null, $err);
        }
        $code = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        $contentType = curl_getinfo($ch, CURLINFO_CONTENT_TYPE);
        curl_close($ch);

        $responseArray = explode("\r\n\r\n", $result);
        $responseArraySize = sizeof($responseArray);
        $respHeader = $responseArray[$responseArraySize-2];
        $respBody = $responseArray[$responseArraySize-1];

        list($reqid, $xLog) = getReqInfo($respHeader);

        $resp = new Qiniu_Response($code, $respBody);
        $resp->Header['Content-Type'] = $contentType;
        $resp->Header["X-Reqid"] = $reqid;
        return array($resp, null);
    }
}

function getReqInfo($headerContent) {
    $headers = explode("\r\n", $headerContent);
    $reqid = null;
    $xLog = null;
    foreach($headers as $header) {
        $header = trim($header);
        if(strpos($header, 'X-Reqid') !== false) {
            list($k, $v) = explode(':', $header);
            $reqid = trim($v);
        } elseif(strpos($header, 'X-Log') !== false) {
            list($k, $v) = explode(':', $header);
            $xLog = trim($v);
        }
    }
    return array($reqid, $xLog);
}

function get_wechat_article_info($url)
{
    $result = array(
        'title' => '',
        'cover' => '',
        'success' => false
    );

    if (empty($url) || strpos($url, 'mp.weixin.qq.com') === false) {
        return $result;
    }

    $ch = curl_init(htmlspecialchars_decode($url));
    curl_setopt_array($ch, [
        CURLOPT_VERBOSE => true,
        CURLOPT_RETURNTRANSFER => true,
        CURLOPT_FOLLOWLOCATION => true,
        CURLOPT_SSL_VERIFYPEER => false,
        CURLOPT_SSL_VERIFYHOST => false,
        CURLOPT_USERAGENT => 'Mozilla/5.0 (Windows NT 10.0; WOW64; Trident/7.0; rv:11.0) like Gecko',
    ]);
    $response = curl_exec($ch);

    preg_match('/var msg_title = \'[\S\s]*?\'.html\\(false\\);/', $response, $matches);
    $result['title'] = empty($matches[0]) ? '' : htmlspecialchars_decode(str_replace(['var msg_title = \'', '\'.html(false);'], '', $matches[0]));
    preg_match('/var msg_cdn_url = "[\S\s]*?";/', $response, $matches);
    $result['cover'] = empty($matches[0]) ? '' : str_replace(['var msg_cdn_url = "', '";'], '', $matches[0]);
    curl_close($ch);

    if (!empty($result['cover'])) {
