class Picture
{
    private $compressLimit;
    private $sizeLimit;

    public function __construct($compressLimit, $sizeLimit)
    {
        $this->compressLimit = $compressLimit;
        $this->sizeLimit = $sizeLimit;
    }

    public function downloadImage($url): string
    {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        curl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);
        $imageData = curl_exec($ch);
        $imageInfo = getimagesizefromstring($imageData);
        curl_close($ch);

        if ($imageInfo[0] > $this->sizeLimit || strlen($imageData) > $this->compressLimit) {
            $imageData = $this->compressImage($imageData);
        }

        return $imageData;
    }

    private function compressImage($imageData): string
    {
        $imageInfo = getimagesizefromstring($imageData);
        $imageType = $imageInfo[2];
        switch ($imageType) {
            case IMAGETYPE_JPEG:
                $imageResource = imagecreatefromjpegFromString($imageData);
                break;
            case IMAGETYPE_PNG:
                $imageResource = imagecreatefrompngFromString($imageData);
                break;
            case IMAGETYPE_GIF:
                $imageResource = imagecreatefromgifFromString($imageData);
                break;
            default:
                return $imageData;
        }

        $quality = 75;
        switch ($imageType) {
            case IMAGETYPE_JPEG:
                imagejpeg($imageResource, null, $quality);
                break;
            case IMAGETYPE_PNG:
                imagepng($imageResource, null, $quality);
                break;
            case IMAGETYPE_GIF:
                imagegif($imageResource, null);
                break;
        }

        ob_start();
        switch ($imageType) {
            case IMAGETYPE_JPEG:
                imagejpeg($imageResource, null, $quality);
                break;
            case IMAGETYPE_PNG:
                imagepng($imageResource, null, $quality);
                break;
            case IMAGETYPE_GIF:
                imagegif($imageResource, null);
                break;
        }
        $compressedImage = ob_get_contents();
        ob_end_clean();
        imagedestroy($imageResource);

        return $compressedImage;
    }
