    fwrite($fp, $headers);
    $response = '';
    while (!feof($fp)) {
        $response .= fgets($fp, 4096);
    }
    fclose($fp);
    $headers = explode("\r\n\r\n", $response)[0];
    $body = explode("\r\n\r\n", $response)[1];
    if (strpos($headers, '200 OK') !== false) {
        $data = $body;
        return true;
    } else {
        $data = '';
        return false;
    }
