        $socket = fsockopen($host, $port, $errno, $errstr, 30);
        if (!$socket) {
            $this->logger && $this->logger->error('Failed to connect to ' . $host . ':' . $port . ' - ' . $errstr);
            return $this->responseFactory->createResponse(500)->withStatus('500 Internal Server Error');
        }

        $requestLine = $method . ' ' . $path . '?' . $query . " HTTP/1.1\r\n";
        $headerLines = [];
        foreach ($headers as $key => $values) {
