        $headerLines[] = 'Connection: close';
        $headerLines[] = 'Content-Length: ' . strlen($body);
        $headerLines[] = 'Content-Type: ' . $request->getHeaderLine('Content-Type');
        $headerLines[] = "\r\n";
        $header = implode("\r\n", $headerLines);
        $data = $requestLine . $header . $body;

        fwrite($socket, $data);
        $response = '';
        while (!feof($socket)) {
            $response .= fread($socket, 1024);
        }
        fclose($socket);

        $this->logger && $this->logger->debug('Received response from ' . $host . ':' . $port);

        $responseHeaders = [];
        $body = '';
        $headers = explode("\r\n", $response);
        foreach ($headers as $header) {
            if (strpos($header, "\r\n") === false) {
                $body .= $header;
            } else {
                $responseHeaders[] = $header;
