        $body = ltrim($body);

        $statusCode = substr($responseHeaders[0], 9, 3);
        $reasonPhrase = http_status_code_to_reason_phrase($statusCode);
        $headers = [];
        foreach (array_slice($responseHeaders, 1, -2, true) as $header) {
            list($key, $value) = explode(':', $header, 2);
            $headers[strtolower($key)] = trim($value);
        }
        $headers['status-code'] = $statusCode;
        $headers['reason-phrase'] = $reasonPhrase;

        return $this->responseFactory->createResponse($statusCode, $reasonPhrase, $headers, $this->streamFactory->createStream($body));
    }
