<?php
namespace MyHttpClient;

use Psr\Http\Client\ClientInterface;
use Psr\Http\Message\RequestInterface;
use Psr\Http\Message\ResponseInterface;
use Psr\Log\LoggerInterface;
use Psr\Http\Message\UriInterface;

class SocketHttpClient implements ClientInterface
{
    private $logger;

    public function __construct(LoggerInterface $logger = null)
    {
        $this->logger = $logger ?: new NullLogger();
    }

    public function sendRequest(RequestInterface $request): ResponseInterface
    {
        $this->logger->debug('Sending request', ['request' => $request]);
        $uri = $request->getUri();
        $method = $request->getMethod();
        $headers = $request->getHeaders();
        $body = $request->getBody()->getContents();

        $socket = $this->createSocket($uri);
        $this->sendHeaders($socket, $method, $uri, $headers, $body);
        $response = $this->receiveResponse($socket);

        $this->logger->debug('Received response', ['response' => $response]);
        return $response;
    }

    private function createSocket(UriInterface $uri): resource
    {
        $scheme = $uri->getScheme();
        $host = $uri->getHost();
        $port = $uri->getPort() ?: ($scheme === 'https' ? 443 : 80);
        $socket = fsockopen($host, $port, $errno, $errstr, 30);
        if (!$socket) {
            $this->logger->error("Failed to connect to {$host}:{$port}", ['errno' => $errno, 'errstr' => $errstr]);
            throw new \RuntimeException("Failed to connect to {$host}:{$port}");
        }
        $this->logger->debug("Connected to {$host}:{$port}");
        return $socket;
    }

    private function sendHeaders(resource $socket, string $method, UriInterface $uri, array $headers, string $body): void
    {
        $path = $uri->getPath() ?: '/';
        $query = $uri->getQuery();
        $host = $uri->getHost();
        $port = $uri->getPort() ?: ($uri->getScheme() === 'https' ? 443 : 80);
        $requestLine = "{$method} {$path}{$query} HTTP/1.1\r\n";
        $hostHeader = "Host: {$host}{$port ? ":{$port}" : ''}\r\n";
        $headers = array_merge(['Host' => $hostHeader], $headers);
        $headerLines = [];
        foreach ($headers as $key => $values) {
            foreach ($values as $value) {
                $headerLines[] = "{$key}: {$value}";
            }
        }
        $headerLines[] = "Connection: close\r\n";
        $headerLines[] = "Content-Length: " . strlen($body) . "\r\n";
        $headerLines[] = "\r\n";
        $data = implode("\r\n", [$requestLine, implode("\r\n", $headerLines), $body]);
        fwrite($socket, $data);
    }

    private function receiveResponse(resource $socket): ResponseInterface
    {
        $headers = [];
        $body = '';
        $headerLine = fgets($socket);
        while ($headerLine !== false && trim($headerLine) !== '') {
            list($key, $value) = explode(':', trim($headerLine), 2);
            $headers[strtolower($key)][] = trim($value);
            $headerLine = fgets($socket);
        }
        $statusCode = $headers['http/1.1'][0];
        $body = '';
        while (!feof($socket)) {
            $body .= fread($socket, 1024);
        }
        fclose($socket);
        return new Response($statusCode, $headers, $body);
    }
}

