    }

    public function withoutHeader(string $name): ResponseInterface
    {
        $newHeaders = $this->headers;
        unset($newHeaders[strtolower($name)]);
        return new self($this->statusCode, $newHeaders, $this->body);
    }

    public function getBody(): BodyInterface
    {
        return new Body($this->body);
    }

    public function withBody(BodyInterface $body): ResponseInterface
    {
        return new self($this->statusCode, $this->headers, $body->getContents());
    }
}

class Body implements BodyInterface
{
