<?php
namespace Http\Client;

use Psr\Http\Client\ClientInterface;
use Psr\Http\Message\RequestInterface;
use Psr\Http\Message\ResponseInterface;
use Psr\Log\LoggerInterface;
use Psr\Http\Message\UriInterface;

class SocketHttpClient implements ClientInterface
{
    private $logger;

    public function __construct(LoggerInterface $logger = null)
    {
        $this->logger = $logger;
    }

    public function sendRequest(RequestInterface $request): ResponseInterface
    {
        $uri = $request->getUri();
        $method = $request->getMethod();
        $headers = $request->getHeaders();
        $body = $request->getBody()->getContents();

        $socket = $this->createSocket($uri);
        $this->sendHeaders($socket, $method, $uri, $headers, $body);
        $response = $this->receiveResponse($socket);

        $this->closeSocket($socket);

        return $response;
    }

    private function createSocket(UriInterface $uri): resource
    {
        $scheme = $uri->getScheme();
        $host = $uri->getHost();
        $port = $uri->getPort() ?: ($scheme === 'https' ? 443 : 80);

        $socket = fsockopen($host, $port, $errno, $errstr, 30);
        if (!$socket) {
            $this->logger->error("Failed to connect to {$host}:{$port}. Error: {$errstr} ({$errno})");
            throw new \RuntimeException("Failed to connect to {$host}:{$port}. Error: {$errstr} ({$errno})");
        }

        return $socket;
    }

    private function sendHeaders(resource $socket, string $method, UriInterface $uri, array $headers, string $body): void
    {
        $path = $uri->getPath() ?: '/';
        $query = $uri->getQuery();
        if ($query) {
            $path .= '?' . $query;
        }

        $requestLine = "{$method} {$path} HTTP/1.1\r\n";
        $hostHeader = "Host: {$uri->getHost()}\r\n";
        $contentLengthHeader = "Content-Length: " . strlen($body) . "\r\n";
        $contentTypeHeader = "Content-Type: " . $request->getHeaderLine('Content-Type') . "\r\n";
        $connectionHeader = "Connection: close\r\n";
        $headersString = implode("\r\n", $headers) . "\r\n\r\n";

        fwrite($socket, $requestLine . $hostHeader . $contentLengthHeader . $contentTypeHeader . $connectionHeader . $headersString . $body);
    }

    private function receiveResponse(resource $socket): ResponseInterface
    {
        $headers = [];
        $body = '';
        $headerLine = '';

        while (($line = fgets($socket)) !== false) {
            $headerLine .= $line;
            if (strpos($line, "\r\n\r\n") !== false) {
                $headers = $this->parseHeaders($headerLine);
                break;
            }
        }

        while (($line = fgets($socket)) !== false && $line !== "\r\n") {
            $body .= $line;
        }

        return $this->createResponse($headers, $body);
    }

    private function parseHeaders(string $headerLine): array
    {
        $headers = [];
        foreach (explode("\r\n", $headerLine) as $line) {
            if (strpos($line, ':') !== false) {
                list($key, $value) = explode(':', $line, 2);
                $headers[trim($key)] = trim($value);
            }
        }
        return $headers;
    }

    private function createResponse(array $headers, string $body): ResponseInterface
    {
        $statusCode = $headers['Status-Line'] ?? 'HTTP/1.1 200 OK';
        $statusCode = explode(' ', $statusCode)[1];

        $response = new class implements ResponseInterface {
            public $headers = [];
            public $body = '';

            public function getProtocolVersion(): string
            {
                return '1.1';
            }

            public function getHeaders(): array
            {
                return $this->headers;
            }

            public function getHeader(string $name): array
            {
                return $this->headers[$name] ?? [];
            }

            public function getHeaderLine(string $name): string
            {
                return implode(', ', $this->getHeader($name));
            }

            public function withProtocolVersion($version): ResponseInterface
            {
                return $this;
            }

            public function withBody($body): ResponseInterface
            {
                $this->body = (string) $body;
                return $this;
            }

            public function withHeaders(array $headers): ResponseInterface
            {
                $this->headers = $headers;
                return $this;
            }

            public function getBody(): BodyInterface
            {
                return new class implements BodyInterface {
                    public $body = '';

                    public function getContents(): string
                    {
                        return $this->body;
                    }

                    public function getIterator(): Traversable
                    {
                        return new ArrayIterator(str_split($this->body));
                    }
                };
            }
        };

        $response->headers = $headers;
        $response->body = $body;
        $response->withHeader('Status-Line', $statusCode);

        return $response;
    }

    private function closeSocket(resource $socket): void
    {
        fclose($socket);
    }
}
