        $this->_fingerprint = md5($session_data);
        if ($this->_get_lock($session_id)) {
            if ($this->_redis->get($this->_key_prefix . $session_id) !== $session_data) {
                $this->_redis->set($this->_key_prefix . $session_id, $session_data);
            }
            $this->_release_lock();
            return true;
        }
        return false;
    }

    public function close() {
        $this->_redis->close();
        return true;
    }

    public function destroy($session_id) {
        $this->_redis->del($this->_key_prefix . $session_id);
        return true;
    }

    public function gc($maxlifetime) {
        return true;
    }

    private function _get_lock($session_id) {
        $this->_lock_key = $this->_key_prefix . 'lock:' . $session_id;
        return $this->_redis->set($this->_lock_key, true, ['nx', 'ex' => 5]);
    }

    private function _release_lock() {
        $this->_redis->del($this->_lock_key);
    }

    private function _fail() {
        return false;
    }

    private function _success() {
        return true;
    }
}
