    {
        return serialize($data);
    }

    protected function unserialize($data)
    {
        return unserialize($data);
    }

    protected function get_ttl($key)
    {
        $info = $this->_redis->info();
        $keys = explode("\n", $info['keys']);
        foreach ($keys as $key) {
            if (strpos($key, $key) === 0) {
                $ttl = $this->_redis->ttl($key);
                return $ttl;
            }
        }
