<?php
namespace App\HttpClientKernel;

use Symfony\Component\HttpClient\HttpClient;
use Symfony\Component\HttpClient\HttpClientException;
use Symfony\Component\HttpKernel\HttpKernelInterface;
use Symfony\Component\HttpKernel\Request;
use Symfony\Component\HttpKernel\Response;
use Symfony\Component\Mime\MimePart;
use Symfony\Component\Mime\MimeTypes;

class HttpClientKernel implements HttpKernelInterface
{
    private $client;

    public function __construct(?HttpClientInterface $client = null)
    {
        if (null === $client) {
            try {
                $client = HttpClient::create();
            } catch (HttpClientException $e) {
                throw new \RuntimeException('The HttpClient component is not installed.');
            }
        }
        $this->client = $client;
    }

    public function handle(Request $request, int $type = HttpKernelInterface::MAIN_REQUEST, bool $catch = true): Response
    {
        $body = $this->getBody($request);
        $headers = $this->getHeaders($request);

        $response = $this->client->request(
            $request->getMethod(),
            $request->getUri(),
            [
                'headers' => $headers,
                'body' => $body ? (string) $body : null,
            ]
        );

        return new Response(
            $response->getContent(),
            $response->getStatusCode(),
            $response->getHeaders()
        );
    }

    private function getBody(Request $request): ?MimePart
    {
        if ($request->getMethod() === 'GET' || $request->getMethod() === 'HEAD') {
            return null;
        }

        $body = $request->getContent();
        if (empty($body)) {
            return null;
