        if (in_array($request->getMethod(), ['GET', 'HEAD'], true)) {
            return null;
        }

        $body = $request->getContent();

        if ($request->getContentType() === 'multipart/form-data') {
            $multipart = new MultipartStream();
            $boundary = $request->getBoundary();
