    private $stdin = null;
    private $env = null;
    private $timeout = null;

    public function runJob(string $job, array $settings = []): array
    {
        $settings += ['stdin' => $job];
        return $this->runProcess($job, $settings);
    }

    public function getHandles(): array
    {
        return [];
    }

    private function runProcess(string $job, array $settings): array
    {
        $descriptorspec = [
            0 => ["pipe", "r"],
            1 => ["pipe", "w"],
            2 => ["pipe", "w"]
        ];

        $process = proc_open($settings['cmd'], $descriptorspec, $pipes, null, $this->env, []);

        $this->process($pipes[0], $job);

        $stdout = '';
        $stderr = '';
        $return_value = 0;

        $timeout = $settings['timeout'] ?? 30;
        $start_time = microtime(true);

        while ($return_value === 0) {
            $status = proc_get_status($process);
            if ($status['running']) {
                if (microtime(true) - $start_time > $timeout) {
                    proc_terminate($process);
                    $return_value = proc_close($process);
                }
            } else {
                $return_value = proc_close($process);
            }
        }

        fclose($pipes[0]);
        fclose($pipes[1]);
        fclose($pipes[2]);

        return [
            'stdout' => $stdout,
            'stderr' => $stderr,
            'return_value' => $return_value
        ];
    }

    private function process(resource $pipe, string $job): void
    {
        if ($this->tempFile) {
            file_put_contents($this->tempFile, $job);
            fwrite($pipe, $this->tempFile);
        } else {
            fwrite($pipe, $job);
        }
    }

    public function cleanup(): void
    {
