class ProcessHelper
{
    public function run(string $command, string $cwd = null): array
    {
        $descriptorspec = [
            0 => ['pipe', 'r'],
            1 => ['pipe', 'w'],
            2 => ['pipe', 'w']
        ];

        $process = proc_open($command, $descriptorspec, $pipes, $cwd);

        if (!is_resource($process)) {
            throw new RuntimeException('Process cannot be started');
        }

        fclose($pipes[0]);

        $output = '';
        $errors = '';
        $exitCode = null;

        while (($buffer = fgets($pipes[1])) !== false) {
            $output .= trim($buffer);
        }

        while (($buffer = fgets($pipes[2])) !== false) {
            $errors .= trim($buffer);
        }

        $exitCode = proc_close($process);

        return [
            $exitCode,
            $output,
            $errors
        ];
