            2 => ['pipe', 'w']
        ];
        $process = proc_open('php', $descriptorspec, $pipes, null, $this->env, $settings);
        if (!is_resource($process)) {
            return false;
        }
        $this->process($pipes[0], $job);
        $output = stream_get_contents($pipes[1]);
        $error = stream_get_contents($pipes[2]);
        $returnVar = proc_close($process);
        fclose($pipes[0]);
        fclose($pipes[1]);
        fclose($pipes[2]);
        return ['output' => $output, 'error' => $error, 'returnVar' => $returnVar];
    }

    private function process($pipe, $job) {
        if ($this->useTemporaryFile()) {
            $this->tempFile = tempnam(sys_get_temp_dir(), 'php');
            file_put_contents($this->tempFile, $job);
            $stdin = $this->tempFile;
        } else {
            $stdin = $job;
        }
        fwrite($pipe, $stdin);
        fflush($pipe);
    }

    private function cleanup() {
        if (isset($this->tempFile) && file_exists($this->tempFile)) {
            unlink($this->tempFile);
        }
    }

    private function useTemporaryFile() {
        return false;
    }
