    private $secretkey_ = '';

    public function send($url, $params = array(), $headers = array()) {
        $curl = curl_init();
        $query = http_build_query($params);
        curl_setopt($curl, CURLOPT_URL, $url . '?' . $query);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl, CURLOPT_HTTPHEADER, array_merge($headers, [
            'Authorization: Bearer ' . $this->accesskey_ . ':' . $this->secretkey_
        ]));
        curl_setopt($curl, CURLOPT_HEADER, true);
        curl_setopt($curl, CURLOPT_NOBODY, false);
        curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, false);
        $response = curl_exec($curl);
        $this->httpCode_ = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        $this->httpDesc_ = curl_getinfo($curl, CURLINFO_HTTP_VERSION_STRING);
        if ($response === false) {
            $error = curl_error($curl);
            curl_close($curl);
            return false;
        }
        $headerSize = curl_getinfo($curl, CURLINFO_HEADER_SIZE);
        $header = substr($response, 0, $headerSize);
        $body = substr($response, $headerSize);
        curl_close($curl);
        $this->header_ = $header;
        $decodedResponse = json_decode($body, true);
        if (json_last_error() === JSON_ERROR_NONE) {
            return $decodedResponse;
