    private $secretkey_ = '';

    public function send($url, $params = array(), $headers = array()) {
        $curl = curl_init();
        $postData = http_build_query($params);
        $headers[] = 'Content-Type: application/x-www-form-urlencoded';
        $headers[] = 'Authorization: Bearer ' . $this->accesskey_ . ':' . $this->secretkey_;
        $this->header_ = implode("\r\n", $headers);
        curl_setopt($curl, CURLOPT_URL, $url);
        curl_setopt($curl, CURLOPT_POST, true);
        curl_setopt($curl, CURLOPT_POSTFIELDS, $postData);
        curl_setopt($curl, CURLOPT_HTTPHEADER, $this->header_);
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($curl, CURLOPT_HEADER, true);
        curl_setopt($curl, CURLOPT_SSL_VERIFYPEER, false);
        $response = curl_exec($curl);
        $this->httpCode_ = curl_getinfo($curl, CURLINFO_HTTP_CODE);
        $this->httpDesc_ = curl_getinfo($curl, CURLINFO_HTTP_VERSION);
        if ($response === false) {
            $error = curl_error($curl);
            curl_close($curl);
            return false;
        }
        $headersSize = curl_getinfo($curl, CURLINFO_HEADER_SIZE);
        $headers = substr($response, 0, $headersSize);
        $body = substr($response, $headersSize);
        curl_close($curl);
        $decodedResponse = json_decode($body, true);
        if (json_last_error() === JSON_ERROR_NONE) {
            return $decodedResponse;
        }
        return false;
    }
