            foreach ($files as $key => $file) {
                $data = fopen($file['tmp_name'], 'r');
                $context = stream_context_create([
                    'http' => [
                        'method' => 'POST',
                        'header' => "Content-Type: multipart/form-data; boundary=" . $this->getBoundary() . "\r\n",
                        'content' => $this->buildMultipartFormData($param, $files),
                    ],
                    'ssl' => [
                        'verify_peer' => $this->ssl_verify,
                        'verify_peer_name' => $this->ssl_verify,
                    ],
                ]);
            }
        }

        $result = file_get_contents($url, false, $context);
        if ($result === false) {
            return false;
        }

        $http_response_header = $http_response_header[0];
        if (strpos($http_response_header, '200 OK') !== false) {
            return $result;
        }

        return false;
    }

    private function getBoundary(): string
    {
        return '----WebKitFormBoundary' . bin2hex(random_bytes(16));
    }

    private function buildMultipartFormData(array $param, array $files): string
    {
        $boundary = $this->getBoundary();
        $data = '';

        foreach ($param as $key => $value) {
            $data .= "--$boundary\r\n";
            $data .= "Content-Disposition: form-data; name=\"$key\"\r\n\r\n";
            $data .= $value . "\r\n";
        }

        foreach ($files as $key => $file) {
            $data .= "--$boundary\r\n";
            $data .= "Content-Disposition: form-data; name=\"$key\"; filename=\"" . basename($file['name']) . "\"\r\n";
            $data .= "Content-Type: " . mime_content_type($file['tmp_name']) . "\r\n\r\n";
            $data .= fread($file['tmp_name'], $file['size']);
            $data .= "\r\n";
        }

        $data .= "--$boundary--\r\n";

        return $data;
    }
