            if ($release['prerelease'] && (!$latestPreRelease || $release['published_at'] > $latestPreRelease['published_at'])) {
                $latestPreRelease = $release;
            }
        }
        return $latestPreRelease ? $latestPreRelease['tag_name'] : '';
