    protected $CURL_OPTS = array(
        CURLOPT_RETURNTRANSFER => 1,
        CURLOPT_SSL_VERIFYPEER => false,
        CURLOPT_TIMEOUT => 10,
        CURLOPT_USERAGENT => 'Facebook PHP SDK v5.0'
    );
    protected $DROP_QUERY_PARAMS = array();
    protected $DOMAIN_MAP = array(
        'facebook' => 'https://graph.facebook.com',
        'instagram' => 'https://graph.instagram.com',
        'messenger' => 'https://graph.messenger.com'
    );
    protected $appId;
    protected $appSecret;
    protected $user;
    protected $signedRequest;
    protected $state;
    protected $accessToken;
    protected $fileUploadSupport = false;
    protected $trustForwarded = false;

    public function makeRequest($url, $params, $ch = null) {
        if (!$ch) {
            $ch = curl_init();
        }
        foreach ($this->CURL_OPTS as $opt => $value) {
            curl_setopt($ch, $opt, $value);
        }
        curl_setopt($ch, CURLOPT_URL, $url);
        if (is_array($params)) {
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));
        }
        $response = curl_exec($ch);
        if (curl_errno($ch)) {
            $error_msg = curl_error($ch);
            $this->errorLog($error_msg);
            return false;
        }
        curl_close($ch);
        return $response;
    }

    protected function _oauthRequest($url, $params) {
        if (isset($this->accessToken)) {
            $params['access_token'] = $this->accessToken;
        }
        $proof = $this->getAppSecretProof($this->accessToken);
        if ($proof) {
            $params['appsecret_proof'] = $proof;
        }
        return $this->makeRequest($url, $params);
    }

    public function getAppSecretProof($access_token) {
        return hash_hmac('sha256', $access_token, $this->appSecret);
    }

    public function errorLog($msg) {
        if (PHP_SAPI === 'cli') {
            echo $msg . PHP_EOL;
        } else {
            error_log($msg);
        }
    }
}
