class BaseFacebook {
    protected $CURL_OPTS = array(
        CURLOPT_RETURNTRANSFER => 1,
        CURLOPT_SSL_VERIFYPEER => 0,
        CURLOPT_TIMEOUT => 10,
        CURLOPT_USERAGENT => 'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/88.0.4324.104 Safari/537.36'
    );
    protected $DROP_QUERY_PARAMS = array();
    protected $DOMAIN_MAP = array(
        'facebook' => 'https://graph.facebook.com',
        'instagram' => 'https://graph.instagram.com',
        // Add other domains as needed
    );
    protected $appId;
    protected $appSecret;
    protected $user;
    protected $signedRequest;
    protected $state;
    protected $accessToken;
    protected $fileUploadSupport = false;
    protected $trustForwarded = false;

    public function makeRequest($url, $params, $ch = null) {
        if (!$ch) {
            $ch = curl_init();
        }
        foreach ($this->CURL_OPTS as $option => $value) {
            curl_setopt($ch, $option, $value);
        }
        if (isset($params['access_token'])) {
            $this->accessToken = $params['access_token'];
        }
        $url = $this->buildUrl($url, $params);
        curl_setopt($ch, CURLOPT_URL, $url);
        $response = curl_exec($ch);
        if (curl_errno($ch)) {
            $this->errorLog(curl_error($ch));
            return false;
        }
        $httpcode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        if ($httpcode != 200) {
            $this->errorLog("HTTP error: $httpcode");
            return false;
        }
        curl_close($ch);
        return $response;
    }

    protected function _oauthRequest($url, $params) {
        $params['access_token'] = $this->accessToken;
        $params['app_secret_proof'] = $this->getAppSecretProof($this->accessToken);
        return $this->makeRequest($url, $params);
    }

    protected function getAppSecretProof($access_token) {
        return hash_hmac('sha256', $access_token, $this->appSecret);
    }

    protected function errorLog($msg) {
        if (PHP_SAPI == 'cli') {
            echo "Error: $msg\n";
        } else {
            error_log($msg);
        }
    }

    protected function buildUrl($url, $params) {
        $url = $this->DOMAIN_MAP[parse_url($url, PHP_URL_HOST)];
        $query = http_build_query(array_diff_key($params, $this->DROP_QUERY_PARAMS));
        return $url . '?' . $query;
    }
}
