class URLChecker {
    private const POLL_INTERVAL_MS = 1000;
    private const CONNECT_TIMEOUT_MS = 5000;

    private $pollInterval;
    private $connectTimeout;

    public function __construct() {
        $this->pollInterval = self::POLL_INTERVAL_MS;
        $this->connectTimeout = self::CONNECT_TIMEOUT_MS;
    }

    public function waitUntilAvailable(int $timeout_in_ms, string $url): self {
        $endTime = time() + $timeout_in_ms / 1000;
        while (time() < $endTime) {
            if ($this->getHTTPResponseCode($url) === 200) {
                return $this;
            }
            sleep(1);
        }
        throw new TimeoutException('Timeout waiting for URL to become available.');
    }

    public function waitUntilUnavailable(int $timeout_in_ms, string $url): self {
        $endTime = time() + $timeout_in_ms / 1000;
        while (time() < $endTime) {
            $responseCode = $this->getHTTPResponseCode($url);
            if ($responseCode !== null && $responseCode !== 200) {
                return $this;
            }
            sleep(1);
        }
        throw new TimeoutException('Timeout waiting for URL to become unavailable.');
    }

    private function getHTTPResponseCode(string $url): ?int {
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_NOBODY, true);
        curl_setopt($ch, CURLOPT_TIMEOUT, $this->connectTimeout / 1000);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_exec($ch);
        $responseCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
        curl_close($ch);
        return $responseCode;
    }
}
